import React, { useCallback, memo, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiBook, FiZap, FiClock } from 'react-icons/fi';
import { PersonalizedInsights } from '../components/PersonalizedInsights';
import { analyticsAPI } from '../services/api';

interface RecentTopic {
  topic: string;
  mastery: number;
  lastStudied: string;
}

const DashboardPage: React.FC = memo(() => {
  const navigate = useNavigate();
  const [recentTopics, setRecentTopics] = useState<RecentTopic[]>([]);
  const [loadingTopics, setLoadingTopics] = useState(true);

  useEffect(() => {
    const fetchTopics = async () => {
      try {
        const response = await analyticsAPI.getInsights();
        const topics = (response.data.masteryByTopic || []).slice(0, 5).map((t: any) => ({
          topic: t.topic,
          mastery: t.mastery,
          lastStudied: new Date().toLocaleDateString(),
        }));
        setRecentTopics(topics);
      } catch (error) {
        console.error('Failed to fetch topics:', error);
      } finally {
        setLoadingTopics(false);
      }
    };
    fetchTopics();
  }, []);

  const navigateTo = useCallback((path: string) => () => navigate(path), [navigate]);

  const navigateToTopic = useCallback((topic: string) => () => {
    navigate(`/chat/dsa?topic=${topic.toLowerCase()}`);
  }, [navigate]);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
          <div className="flex items-center gap-3">
            <img src="/logo.svg" alt="Learning Copilot" className="w-10 h-10" />
            <h1 className="text-2xl font-bold text-gray-900">Learning Copilot</h1>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 py-8">
        {/* Welcome Section */}
        <div className="bg-gradient-to-r from-blue-600 via-blue-700 to-purple-700 text-white rounded-2xl p-8 mb-8 shadow-xl relative overflow-hidden">
          <div className="absolute inset-0 overflow-hidden pointer-events-none">
            <div className="absolute -top-20 -right-20 w-60 h-60 bg-white/10 rounded-full blur-2xl" />
            <div className="absolute -bottom-20 -left-20 w-60 h-60 bg-purple-500/20 rounded-full blur-2xl" />
          </div>
          <div className="relative">
            <h2 className="text-3xl font-bold mb-2">Welcome back! ðŸ‘‹</h2>
            <p className="text-blue-100 text-lg">
              Continue your learning journey and master core CS and ECE concepts
            </p>
          </div>
        </div>

        {/* Recent Topics */}
        {!loadingTopics && recentTopics.length > 0 && (
          <div className="mb-8">
            <h2 className="text-lg font-bold text-gray-900 mb-4 flex items-center gap-2">
              <FiClock className="text-blue-600" /> Continue Learning
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-3">
              {recentTopics.map((topic, idx) => {
                const progressColor = topic.mastery < 40 ? 'bg-red-50 border-red-200' : topic.mastery < 70 ? 'bg-yellow-50 border-yellow-200' : 'bg-green-50 border-green-200';
                const progressBarColor = topic.mastery < 40 ? 'bg-red-500' : topic.mastery < 70 ? 'bg-yellow-500' : 'bg-green-500';

                return (
                  <div
                    key={idx}
                    onClick={navigateToTopic(topic.topic)}
                    className={`${progressColor} border rounded-lg p-3 cursor-pointer hover:shadow-md transition-all`}
                  >
                    <p className="font-semibold text-gray-900 text-sm capitalize mb-2">{topic.topic}</p>
                    <div className="w-full bg-gray-300 rounded-full h-1.5 mb-2">
                      <div
                        className={`${progressBarColor} h-1.5 rounded-full`}
                        style={{ width: `${topic.mastery}%` }}
                      />
                    </div>
                    <p className="text-xs text-gray-600">{topic.mastery}% mastery</p>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* Explore Courses Action */}
        <div className="mb-8">
          <div
            onClick={navigateTo('/courses')}
            className="bg-white rounded-lg shadow p-6 cursor-pointer hover:shadow-lg transition-all group"
          >
            <div className="w-14 h-14 bg-blue-100 rounded-xl flex items-center justify-center mb-4 group-hover:bg-blue-600 transition-colors">
              <FiBook className="text-2xl text-blue-600 group-hover:text-white transition-colors" />
            </div>
            <h3 className="text-xl font-bold mb-2 text-gray-900">Explore Courses</h3>
            <p className="text-gray-600">
              Browse 35+ CS and ECE courses
            </p>
          </div>
        </div>

        {/* Personalized Insights */}
        <div className="bg-white rounded-lg shadow p-6 mb-8">
          <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
            <FiZap className="text-yellow-500" /> Your Personalized Insights
          </h3>
          <PersonalizedInsights />
        </div>

        {/* Learning Tips */}
        <div className="bg-gradient-to-r from-indigo-50 to-blue-50 rounded-lg border border-indigo-200 p-6">
          <h3 className="text-lg font-bold text-gray-900 mb-4">ðŸ’¡ Learning Tips</h3>
          <ul className="space-y-2 text-sm text-gray-700">
            <li>âœ“ Study during your peak focus hours for better retention</li>
            <li>âœ“ Use the timer to track focused study sessions</li>
            <li>âœ“ Ask the AI tutor specific questions about topics</li>
            <li>âœ“ Review weak topics regularly to improve mastery</li>
          </ul>
        </div>
      </main>
    </div>
  );
});

export default DashboardPage;
