import React, { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiArrowLeft, FiCheck, FiX, FiRefreshCw } from 'react-icons/fi';

interface Question {
  id: number;
  question: string;
  options: string[];
  correct: number;
  explanation: string;
}

interface QuizState {
  stage: 'setup' | 'quiz' | 'results';
  selectedTopic: string;
  difficulty: 'easy' | 'medium' | 'hard';
  questions: Question[];
  currentQuestion: number;
  answers: number[];
  score: number;
}

const QuizPage: React.FC = () => {
  const navigate = useNavigate();
  const [quiz, setQuiz] = useState<QuizState>({
    stage: 'setup',
    selectedTopic: '',
    difficulty: 'medium',
    questions: [],
    currentQuestion: 0,
    answers: [],
    score: 0,
  });

  const topics = ['Arrays', 'Linked Lists', 'Trees', 'Graphs', 'Sorting', 'Searching', 'Dynamic Programming', 'Stacks & Queues'];

  const generateQuestions = useCallback((topic: string, difficulty: string): Question[] => {
    const questionBank: { [key: string]: { [key: string]: Question[] } } = {
      'Arrays': {
        'easy': [
          {
            id: 1,
            question: 'What is the time complexity of accessing an element in an array?',
            options: ['O(n)', 'O(1)', 'O(log n)', 'O(n²)'],
            correct: 1,
            explanation: 'Arrays provide constant time access to elements using index. Time complexity is O(1).',
          },
          {
            id: 2,
            question: 'Which operation is most efficient in an array?',
            options: ['Insertion at beginning', 'Deletion from middle', 'Access by index', 'Searching'],
            correct: 2,
            explanation: 'Accessing elements by index is O(1), making it the most efficient operation.',
          },
        ],
        'medium': [
          {
            id: 3,
            question: 'What is the time complexity of inserting an element at the beginning of an array?',
            options: ['O(1)', 'O(n)', 'O(log n)', 'O(n log n)'],
            correct: 1,
            explanation: 'Inserting at the beginning requires shifting all elements, resulting in O(n) time complexity.',
          },
        ],
        'hard': [
          {
            id: 4,
            question: 'In a 2D array of size m×n, what is the space complexity?',
            options: ['O(1)', 'O(m+n)', 'O(m*n)', 'O(log(m*n))'],
            correct: 2,
            explanation: 'A 2D array stores m*n elements, so space complexity is O(m*n).',
          },
        ],
      },
      'Trees': {
        'easy': [
          {
            id: 1,
            question: 'What is the root of a tree?',
            options: ['The deepest node', 'The topmost node', 'The leftmost node', 'Any node'],
            correct: 1,
            explanation: 'The root is the topmost node in a tree structure with no parent.',
          },
        ],
        'medium': [
          {
            id: 2,
            question: 'What is the height of a tree with only root node?',
            options: ['0', '1', '-1', 'Undefined'],
            correct: 0,
            explanation: 'A tree with only the root node has height 0.',
          },
        ],
        'hard': [
          {
            id: 3,
            question: 'What is the time complexity of searching in a balanced BST?',
            options: ['O(n)', 'O(log n)', 'O(n²)', 'O(1)'],
            correct: 1,
            explanation: 'In a balanced BST, search takes O(log n) time due to binary elimination.',
          },
        ],
      },
    };

    return questionBank[topic]?.[difficulty] || questionBank[topic]?.['easy'] || [];
  }, []);

  const handleStartQuiz = useCallback(() => {
    if (!quiz.selectedTopic) {
      alert('Please select a topic');
      return;
    }
    const questions = generateQuestions(quiz.selectedTopic, quiz.difficulty);
    setQuiz(prev => ({
      ...prev,
      stage: 'quiz',
      questions,
      answers: new Array(questions.length).fill(-1),
    }));
  }, [quiz.selectedTopic, quiz.difficulty, generateQuestions]);

  const handleAnswerSelect = useCallback((optionIndex: number) => {
    const newAnswers = [...quiz.answers];
    newAnswers[quiz.currentQuestion] = optionIndex;
    setQuiz(prev => ({ ...prev, answers: newAnswers }));
  }, [quiz.answers, quiz.currentQuestion]);

  const handleNextQuestion = useCallback(() => {
    if (quiz.currentQuestion < quiz.questions.length - 1) {
      setQuiz(prev => ({ ...prev, currentQuestion: prev.currentQuestion + 1 }));
    } else {
      const score = quiz.answers.reduce((acc, answer, idx) => {
        return acc + (answer === quiz.questions[idx].correct ? 1 : 0);
      }, 0);
      setQuiz(prev => ({ ...prev, stage: 'results', score }));
    }
  }, [quiz.currentQuestion, quiz.questions.length, quiz.answers, quiz.questions]);

  const handleRetakeQuiz = useCallback(() => {
    setQuiz(prev => ({
      ...prev,
      stage: 'setup',
      currentQuestion: 0,
      answers: [],
      score: 0,
    }));
  }, []);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm sticky top-0 z-10">
        <div className="max-w-4xl mx-auto px-4 py-4 flex items-center gap-4">
          <button
            onClick={() => navigate('/dashboard')}
            className="p-2 hover:bg-gray-100 rounded-lg transition-colors"
          >
            <FiArrowLeft className="text-2xl text-gray-600" />
          </button>
          <h1 className="text-2xl font-bold text-gray-900">Custom Quiz Builder</h1>
        </div>
      </header>

      <main className="max-w-4xl mx-auto px-4 py-8">
        {/* Setup Stage */}
        {quiz.stage === 'setup' && (
          <div className="bg-white rounded-lg shadow p-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">Create Your Quiz</h2>

            <div className="mb-6">
              <label className="block text-sm font-semibold text-gray-700 mb-3">Select Topic</label>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
                {topics.map(topic => (
                  <button
                    key={topic}
                    onClick={() => setQuiz(prev => ({ ...prev, selectedTopic: topic }))}
                    className={`p-3 rounded-lg font-medium transition-all ${
                      quiz.selectedTopic === topic
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    {topic}
                  </button>
                ))}
              </div>
            </div>

            <div className="mb-8">
              <label className="block text-sm font-semibold text-gray-700 mb-3">Difficulty Level</label>
              <div className="flex gap-4">
                {(['easy', 'medium', 'hard'] as const).map(level => (
                  <button
                    key={level}
                    onClick={() => setQuiz(prev => ({ ...prev, difficulty: level }))}
                    className={`px-6 py-2 rounded-lg font-medium transition-all capitalize ${
                      quiz.difficulty === level
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    {level}
                  </button>
                ))}
              </div>
            </div>

            <button
              onClick={handleStartQuiz}
              className="w-full bg-blue-600 text-white py-3 rounded-lg font-bold hover:bg-blue-700 transition-colors"
            >
              Start Quiz
            </button>
          </div>
        )}

        {/* Quiz Stage */}
        {quiz.stage === 'quiz' && quiz.questions.length > 0 && (
          <div className="bg-white rounded-lg shadow p-8">
            <div className="mb-6">
              <div className="flex justify-between items-center mb-2">
                <span className="text-sm font-semibold text-gray-600">
                  Question {quiz.currentQuestion + 1} of {quiz.questions.length}
                </span>
                <span className="text-sm font-semibold text-blue-600">
                  {quiz.selectedTopic} - {quiz.difficulty}
                </span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div
                  className="bg-blue-600 h-2 rounded-full transition-all"
                  style={{ width: `${((quiz.currentQuestion + 1) / quiz.questions.length) * 100}%` }}
                />
              </div>
            </div>

            <h3 className="text-xl font-bold text-gray-900 mb-6">
              {quiz.questions[quiz.currentQuestion].question}
            </h3>

            <div className="space-y-3 mb-8">
              {quiz.questions[quiz.currentQuestion].options.map((option, idx) => (
                <button
                  key={idx}
                  onClick={() => handleAnswerSelect(idx)}
                  className={`w-full p-4 rounded-lg text-left font-medium transition-all ${
                    quiz.answers[quiz.currentQuestion] === idx
                      ? 'bg-blue-600 text-white'
                      : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                  }`}
                >
                  {option}
                </button>
              ))}
            </div>

            <button
              onClick={handleNextQuestion}
              disabled={quiz.answers[quiz.currentQuestion] === -1}
              className="w-full bg-blue-600 text-white py-3 rounded-lg font-bold hover:bg-blue-700 transition-colors disabled:bg-gray-400 disabled:cursor-not-allowed"
            >
              {quiz.currentQuestion === quiz.questions.length - 1 ? 'Finish Quiz' : 'Next Question'}
            </button>
          </div>
        )}

        {/* Results Stage */}
        {quiz.stage === 'results' && (
          <div className="bg-white rounded-lg shadow p-8">
            <h2 className="text-2xl font-bold text-gray-900 mb-6">Quiz Results</h2>

            <div className="text-center mb-8">
              <div className="text-6xl font-bold text-blue-600 mb-2">
                {Math.round((quiz.score / quiz.questions.length) * 100)}%
              </div>
              <p className="text-xl text-gray-600">
                You scored {quiz.score} out of {quiz.questions.length}
              </p>
            </div>

            <div className="space-y-4 mb-8">
              {quiz.questions.map((question, idx) => (
                <div key={idx} className="border rounded-lg p-4">
                  <div className="flex items-start gap-3 mb-2">
                    {quiz.answers[idx] === question.correct ? (
                      <FiCheck className="text-green-600 text-xl flex-shrink-0 mt-1" />
                    ) : (
                      <FiX className="text-red-600 text-xl flex-shrink-0 mt-1" />
                    )}
                    <div className="flex-1">
                      <p className="font-semibold text-gray-900">{question.question}</p>
                      <p className="text-sm text-gray-600 mt-1">
                        Your answer: <span className="font-medium">{question.options[quiz.answers[idx]]}</span>
                      </p>
                      {quiz.answers[idx] !== question.correct && (
                        <p className="text-sm text-green-600 mt-1">
                          Correct answer: <span className="font-medium">{question.options[question.correct]}</span>
                        </p>
                      )}
                      <p className="text-sm text-gray-700 mt-2 bg-blue-50 p-2 rounded">
                        {question.explanation}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            <button
              onClick={handleRetakeQuiz}
              className="w-full bg-blue-600 text-white py-3 rounded-lg font-bold hover:bg-blue-700 transition-colors flex items-center justify-center gap-2"
            >
              <FiRefreshCw /> Retake Quiz
            </button>
          </div>
        )}
      </main>
    </div>
  );
};

export default QuizPage;
