import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiTarget, FiClock, FiZap, FiTrendingUp, FiAward, FiSun, FiPlay, FiPause, FiX, FiBell, FiCheck } from 'react-icons/fi';
import { analyticsAPI } from '../services/api';

interface InsightsData {
  learningScore: number;
  weeklyImprovement: number;
  weakestTopic: {
    name: string;
    mastery: number;
    goal: number;
  };
  peakFocusTime: {
    range: string;
    score: number;
  };
  optimalDuration: {
    value: string;
    avgAccuracy: number;
  };
  masteryByTopic: Array<{
    topic: string;
    mastery: number;
    sessionsCount: number;
  }>;
  totalSessions: number;
  totalStudyTime: number;
}

interface TimerState {
  active: boolean;
  topic: string;
  elapsedSeconds: number;
  startTime?: Date;
}

export const PersonalizedInsights: React.FC = () => {
  const navigate = useNavigate();
  const [insights, setInsights] = useState<InsightsData | null>(null);
  const [loading, setLoading] = useState(true);
  const [timer, setTimer] = useState<TimerState>({ active: false, topic: '', elapsedSeconds: 0 });
  const [showTimerModal, setShowTimerModal] = useState(false);
  const [selectedTopic, setSelectedTopic] = useState('trees');
  const [reminderSet, setReminderSet] = useState(false);
  const [showReminderToast, setShowReminderToast] = useState(false);

  // Fetch insights data
  const fetchInsights = useCallback(async () => {
    try {
      const response = await analyticsAPI.getInsights();
      setInsights(response.data);
    } catch (error) {
      console.error('Failed to fetch insights:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  // Check timer status on mount
  const checkTimerStatus = useCallback(async () => {
    try {
      const response = await analyticsAPI.getTimerStatus();
      if (response.data.active) {
        setTimer({
          active: true,
          topic: response.data.topic,
          elapsedSeconds: response.data.elapsedSeconds,
          startTime: new Date(response.data.startTime),
        });
      }
    } catch (error) {
      console.error('Failed to check timer:', error);
    }
  }, []);

  useEffect(() => {
    fetchInsights();
    checkTimerStatus();
  }, [fetchInsights, checkTimerStatus]);

  // Timer tick effect
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (timer.active) {
      interval = setInterval(() => {
        setTimer(prev => ({
          ...prev,
          elapsedSeconds: prev.elapsedSeconds + 1,
        }));
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [timer.active]);

  // Format time display
  const formatTime = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Start timer
  const handleStartTimer = async () => {
    try {
      await analyticsAPI.startTimer(selectedTopic, 'dsa');
      setTimer({ active: true, topic: selectedTopic, elapsedSeconds: 0, startTime: new Date() });
      setShowTimerModal(false);
    } catch (error: any) {
      console.error('Failed to start timer:', error);
      if (error.response?.data?.error === 'Timer already running') {
        alert('A timer is already running!');
      }
    }
  };

  // Stop timer
  const handleStopTimer = async () => {
    try {
      const focusScore = Math.min(100, 70 + Math.floor(timer.elapsedSeconds / 60));
      const response = await analyticsAPI.stopTimer(75, focusScore);
      setTimer({ active: false, topic: '', elapsedSeconds: 0 });

      // Refresh insights to show updated mastery
      fetchInsights();

      alert(`Session completed! You studied ${selectedTopic} for ${Math.floor(response.data.duration)} minutes. Mastery increased by ${response.data.masteryIncrease}%!`);
    } catch (error) {
      console.error('Failed to stop timer:', error);
    }
  };

  // Handle practice click - navigate to chat with topic
  const handlePractice = () => {
    if (insights?.weakestTopic) {
      navigate(`/chat/dsa?topic=${insights.weakestTopic.name.toLowerCase()}`);
    }
  };

  // Handle set reminder
  const handleSetReminder = () => {
    if ('Notification' in window) {
      Notification.requestPermission().then(permission => {
        if (permission === 'granted') {
          setReminderSet(true);
          setShowReminderToast(true);
          setTimeout(() => setShowReminderToast(false), 3000);

          // Schedule notification for next optimal time (demo: 10 seconds)
          setTimeout(() => {
            new Notification('ðŸ“š Study Time!', {
              body: `It's your peak focus time (${insights?.peakFocusTime.range}). Start learning now!`,
              icon: '/favicon.ico',
            });
          }, 10000);
        }
      });
    }
  };

  if (loading) {
    return (
      <div className="space-y-4">
        <div className="bg-gray-200 animate-pulse rounded-xl h-32" />
        <div className="bg-gray-200 animate-pulse rounded-xl h-24" />
        <div className="bg-gray-200 animate-pulse rounded-xl h-24" />
        <div className="bg-gray-200 animate-pulse rounded-xl h-24" />
      </div>
    );
  }

  const learningScore = insights?.learningScore || 72;

  return (
    <div className="space-y-6">
      {/* Reminder Toast */}
      {showReminderToast && (
        <div className="fixed top-4 right-4 bg-green-500 text-white px-4 py-3 rounded-lg shadow-lg flex items-center gap-2 z-50 animate-slide-in">
          <FiCheck /> Reminder set for {insights?.peakFocusTime.range}!
        </div>
      )}

      {/* Active Timer Banner */}
      {timer.active && (
        <div className="bg-gradient-to-r from-green-500 to-emerald-600 rounded-xl p-4 text-white animate-pulse-slow">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="w-12 h-12 bg-white/20 rounded-full flex items-center justify-center">
                <FiClock className="text-2xl" />
              </div>
              <div>
                <div className="text-sm opacity-80">Currently Studying</div>
                <div className="text-xl font-bold capitalize">{timer.topic}</div>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <div className="text-4xl font-mono font-bold">
                {formatTime(timer.elapsedSeconds)}
              </div>
              <button
                onClick={handleStopTimer}
                className="bg-white/20 hover:bg-white/30 p-3 rounded-full transition-colors"
              >
                <FiPause className="text-xl" />
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Learning Score Overview */}
      <div className="bg-gradient-to-r from-purple-600 to-indigo-600 rounded-xl p-5 text-white">
        <div className="flex items-center justify-between">
          <div>
            <div className="flex items-center gap-2 mb-1">
              <FiAward className="text-yellow-300" />
              <span className="text-purple-200 text-sm font-medium">Your Learning Score</span>
            </div>
            <div className="text-4xl font-bold">{learningScore}/100</div>
            <div className="flex items-center gap-1 mt-1 text-green-300 text-sm">
              <FiTrendingUp />
              <span>+{insights?.weeklyImprovement || 5} points this week</span>
            </div>
          </div>
          <div className="relative w-20 h-20">
            <svg className="w-20 h-20 transform -rotate-90">
              <circle cx="40" cy="40" r="35" stroke="rgba(255,255,255,0.2)" strokeWidth="6" fill="none" />
              <circle 
                cx="40" cy="40" r="35" 
                stroke="white" 
                strokeWidth="6" 
                fill="none"
                strokeDasharray={`${learningScore * 2.2} 220`}
                strokeLinecap="round"
                className="transition-all duration-1000"
              />
            </svg>
            <div className="absolute inset-0 flex items-center justify-center">
              <FiZap className="text-yellow-300 text-2xl" />
            </div>
          </div>
        </div>
        <div className="mt-4 grid grid-cols-2 gap-4 pt-4 border-t border-white/20">
          <div>
            <div className="text-2xl font-bold">{insights?.totalSessions || 0}</div>
            <div className="text-purple-200 text-sm">Total Sessions</div>
          </div>
          <div>
            <div className="text-2xl font-bold">{Math.round((insights?.totalStudyTime || 0) / 60)}h</div>
            <div className="text-purple-200 text-sm">Study Time</div>
          </div>
        </div>
      </div>

      {/* Personalized Insights Cards */}
      <div className="grid gap-4">
        {/* Weakest Topic Card */}
        <div className="bg-gradient-to-r from-orange-50 to-amber-50 border border-orange-200 rounded-xl p-4 hover:shadow-md transition-all duration-200">
          <div className="flex items-start gap-4">
            <div className="bg-orange-500 w-12 h-12 rounded-xl flex items-center justify-center text-white flex-shrink-0 shadow-lg">
              <FiTarget className="text-xl" />
            </div>
            <div className="flex-1 min-w-0">
              <div className="flex items-start justify-between gap-2">
                <h4 className="font-bold text-gray-900">Focus on {insights?.weakestTopic.name} (Lowest Mastery)</h4>
                <div className="text-right flex-shrink-0">
                  <div className="text-lg font-bold text-gray-900">{insights?.weakestTopic.mastery}%</div>
                  <div className="text-xs text-gray-500">Mastery</div>
                </div>
              </div>
              <p className="text-gray-600 text-sm mt-1">
                {insights?.weakestTopic.name} is your weakest area. Dedicate more time to mastering concepts like binary trees, BST, and traversals.
              </p>

              {/* Progress Bar */}
              <div className="mt-3">
                <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
                  <div 
                    className="h-full bg-gradient-to-r from-orange-400 to-orange-600 rounded-full transition-all duration-1000"
                    style={{ width: `${insights?.weakestTopic.mastery || 35}%` }}
                  />
                </div>
                <div className="flex justify-between text-xs text-gray-500 mt-1">
                  <span>Current</span>
                  <span>Goal: {insights?.weakestTopic.goal}%</span>
                </div>
              </div>

              <button 
                onClick={handlePractice}
                className="mt-3 text-sm font-medium text-orange-600 hover:text-orange-800 transition-colors hover:underline flex items-center gap-1"
              >
                <FiPlay className="text-xs" /> Practice {insights?.weakestTopic.name} â†’
              </button>
            </div>
          </div>
        </div>

        {/* Peak Focus Time Card */}
        <div className="bg-gradient-to-r from-green-50 to-emerald-50 border border-green-200 rounded-xl p-4 hover:shadow-md transition-all duration-200">
          <div className="flex items-start gap-4">
            <div className="bg-green-500 w-12 h-12 rounded-xl flex items-center justify-center text-white flex-shrink-0 shadow-lg">
              <FiSun className="text-xl" />
            </div>
            <div className="flex-1 min-w-0">
              <div className="flex items-start justify-between gap-2">
                <h4 className="font-bold text-gray-900">Peak Focus: {insights?.peakFocusTime.range}</h4>
                <div className="text-right flex-shrink-0">
                  <div className="text-lg font-bold text-gray-900">{insights?.peakFocusTime.range}</div>
                  <div className="text-xs text-gray-500">Best Study Time</div>
                </div>
              </div>
              <p className="text-gray-600 text-sm mt-1">
                Your focus score is {insights?.peakFocusTime.score}% during these hours. Schedule important learning sessions then for maximum retention.
              </p>

              <button 
                onClick={handleSetReminder}
                disabled={reminderSet}
                className={`mt-3 text-sm font-medium transition-colors flex items-center gap-1 ${
                  reminderSet 
                    ? 'text-green-600 cursor-default' 
                    : 'text-green-600 hover:text-green-800 hover:underline'
                }`}
              >
                {reminderSet ? (
                  <><FiCheck className="text-xs" /> Reminder Set!</>
                ) : (
                  <><FiBell className="text-xs" /> Set Reminder â†’</>
                )}
              </button>
            </div>
          </div>
        </div>

        {/* Optimal Duration Card */}
        <div className="bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-200 rounded-xl p-4 hover:shadow-md transition-all duration-200">
          <div className="flex items-start gap-4">
            <div className="bg-blue-500 w-12 h-12 rounded-xl flex items-center justify-center text-white flex-shrink-0 shadow-lg">
              <FiClock className="text-xl" />
            </div>
            <div className="flex-1 min-w-0">
              <div className="flex items-start justify-between gap-2">
                <h4 className="font-bold text-gray-900">{insights?.optimalDuration.value} Sessions Work Best</h4>
                <div className="text-right flex-shrink-0">
                  <div className="text-lg font-bold text-gray-900">{insights?.optimalDuration.value}</div>
                  <div className="text-xs text-gray-500">Optimal Duration</div>
                </div>
              </div>
              <p className="text-gray-600 text-sm mt-1">
                Shorter, focused sessions work better for your learning style (avg {insights?.optimalDuration.avgAccuracy}% accuracy). Take 5-minute breaks between sessions.
              </p>

              <button 
                onClick={() => setShowTimerModal(true)}
                disabled={timer.active}
                className={`mt-3 text-sm font-medium transition-colors flex items-center gap-1 ${
                  timer.active 
                    ? 'text-gray-400 cursor-not-allowed' 
                    : 'text-blue-600 hover:text-blue-800 hover:underline'
                }`}
              >
                <FiPlay className="text-xs" /> {timer.active ? 'Timer Running...' : 'Start Timer â†’'}
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Topic Mastery Overview */}
      {insights?.masteryByTopic && (
        <div className="bg-white rounded-xl p-4 border border-gray-200">
          <h4 className="font-semibold text-gray-800 mb-4 flex items-center gap-2">
            <FiTrendingUp className="text-blue-500" /> All Topic Mastery
          </h4>
          <div className="space-y-3">
            {insights.masteryByTopic.map((topic, idx) => (
              <div key={idx} className="flex items-center gap-3">
                <div className="w-28 text-sm text-gray-600 truncate">{topic.topic}</div>
                <div className="flex-1 h-2 bg-gray-100 rounded-full overflow-hidden">
                  <div 
                    className={`h-full rounded-full transition-all duration-500 ${
                      topic.mastery >= 80 ? 'bg-green-500' :
                      topic.mastery >= 60 ? 'bg-blue-500' :
                      topic.mastery >= 40 ? 'bg-yellow-500' : 'bg-orange-500'
                    }`}
                    style={{ width: `${topic.mastery}%` }}
                  />
                </div>
                <div className="w-12 text-right text-sm font-medium text-gray-700">{topic.mastery}%</div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Timer Modal */}
      {showTimerModal && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <div className="bg-white rounded-2xl p-6 w-full max-w-md mx-4 animate-scale-in">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-xl font-bold text-gray-900">Start Study Session</h3>
              <button 
                onClick={() => setShowTimerModal(false)}
                className="p-2 hover:bg-gray-100 rounded-full"
              >
                <FiX />
              </button>
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Select Topic to Study
              </label>
              <select
                value={selectedTopic}
                onChange={(e) => setSelectedTopic(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                {insights?.masteryByTopic.map((topic, idx) => (
                  <option key={idx} value={topic.topic.toLowerCase().replace(' ', '-')}>
                    {topic.topic} ({topic.mastery}% mastery)
                  </option>
                ))}
              </select>
            </div>

            <div className="bg-blue-50 rounded-lg p-4 mb-4">
              <div className="flex items-center gap-2 text-blue-700">
                <FiZap />
                <span className="text-sm font-medium">Recommended: {insights?.optimalDuration.value} session</span>
              </div>
              <p className="text-xs text-blue-600 mt-1">
                Based on your learning patterns, shorter sessions with breaks work best for you.
              </p>
            </div>

            <div className="flex gap-3">
              <button
                onClick={() => setShowTimerModal(false)}
                className="flex-1 px-4 py-3 border border-gray-300 rounded-lg text-gray-700 hover:bg-gray-50 transition-colors"
              >
                Cancel
              </button>
              <button
                onClick={handleStartTimer}
                className="flex-1 px-4 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors flex items-center justify-center gap-2"
              >
                <FiPlay /> Start Timer
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
