import axios, { AxiosInstance, AxiosRequestConfig, AxiosResponse, InternalAxiosRequestConfig } from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

// Create axios instance with optimized config
const api: AxiosInstance = axios.create({
  baseURL: API_BASE_URL,
  timeout: 15000,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Request interceptor to add auth token
api.interceptors.request.use(
  (config: InternalAxiosRequestConfig) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error: unknown) => Promise.reject(error)
);

// Response interceptor for error handling
api.interceptors.response.use(
  (response: AxiosResponse) => response,
  (error: unknown) => {
    const axiosError = error as { response?: { status: number } };
    if (axiosError.response?.status === 401) {
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

// Simple cache for GET requests
const cache = new Map<string, { data: any; timestamp: number }>();
const CACHE_DURATION = 30000; // 30 seconds

// Cached GET request
export const cachedGet = async <T>(url: string, config?: AxiosRequestConfig): Promise<T> => {
  const cacheKey = url + JSON.stringify(config?.params || {});
  const cached = cache.get(cacheKey);
  
  if (cached && Date.now() - cached.timestamp < CACHE_DURATION) {
    return cached.data;
  }
  
  const response = await api.get<T>(url, config);
  cache.set(cacheKey, { data: response.data, timestamp: Date.now() });
  return response.data;
};

// Clear cache
export const clearCache = () => cache.clear();

// API methods
export const authAPI = {
  login: (email: string, password: string) =>
    api.post('/api/auth/login', { email, password }),
  register: (email: string, password: string, name: string) =>
    api.post('/api/auth/register', { email, password, name }),
};

export const chatAPI = {
  sendMessage: (courseId: string, message: string, conversationId?: string) =>
    api.post('/api/chat/message', { courseId, message, conversationId }),
  getHistory: (conversationId: string) =>
    api.get(`/api/chat/history/${conversationId}`),
};

export const coursesAPI = {
  getAll: () => cachedGet<{ courses: any[] }>('/api/courses'),
  getById: (courseId: string) => cachedGet<any>(`/api/courses/${courseId}`),
  enroll: (courseId: string) => api.post(`/api/courses/${courseId}/enroll`),
};

export const analyticsAPI = {
  getDashboard: () => cachedGet<any>('/api/analytics/dashboard'),
  getExamReadiness: (courseId: string) =>
    cachedGet<any>(`/api/analytics/exam-readiness/${courseId}`),
  logSession: (data: any) => api.post('/api/analytics/session', data),
  
  // Personalized insights
  getInsights: () => api.get('/api/analytics/insights'),
  
  // Study timer
  startTimer: (topic: string, courseId: string) => 
    api.post('/api/analytics/timer/start', { topic, courseId }),
  stopTimer: (accuracy?: number, focusScore?: number) => 
    api.post('/api/analytics/timer/stop', { accuracy, focusScore }),
  getTimerStatus: () => api.get('/api/analytics/timer/status'),
  
  // Mastery updates
  updateMastery: (topic: string, score: number, timeSpent: number) =>
    api.post('/api/analytics/mastery/update', { topic, score, timeSpent }),
};

export const learningAPI = {
  getProfile: () => cachedGet<any>('/api/learning/profile'),
  updateProfile: (data: any) => api.put('/api/learning/profile', data),
  getMastery: (courseId: string) =>
    cachedGet<any>(`/api/learning/mastery/${courseId}`),
};

export default api;
