import React, { useEffect, useState, useCallback, memo } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiLogOut, FiBook, FiMessageSquare, FiZap } from 'react-icons/fi';
import { analyticsAPI } from '../services/api';
import { PersonalizedInsights } from '../components/PersonalizedInsights';

interface DashboardPageProps {
  onLogout: () => void;
}

interface Analytics {
  thisWeek: {
    sessions: number;
    hours: number;
    accuracy: number;
    conceptsMastered: number;
  };
  recommendations: string[];
}

// Skeleton loader component
const Skeleton = ({ className = '' }: { className?: string }) => (
  <div className={`bg-gray-200 animate-pulse rounded ${className}`} />
);

const DashboardPage: React.FC<DashboardPageProps> = memo(({ onLogout }) => {
  const navigate = useNavigate();
  const [analytics, setAnalytics] = useState<Analytics | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;

    const fetchAnalytics = async () => {
      try {
        const data = await analyticsAPI.getDashboard();
        if (mounted) setAnalytics(data);
      } catch (error) {
        console.error('Failed to fetch analytics:', error);
      } finally {
        if (mounted) setLoading(false);
      }
    };

    fetchAnalytics();
    return () => { mounted = false; };
  }, []);

  const handleLogout = useCallback(() => {
    onLogout();
    navigate('/login');
  }, [onLogout, navigate]);

  const navigateTo = useCallback((path: string) => () => navigate(path), [navigate]);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl flex items-center justify-center">
              <span className="text-white font-bold">LC</span>
            </div>
            <h1 className="text-2xl font-bold text-gray-900">Learning Copilot</h1>
          </div>
          <button
            onClick={handleLogout}
            className="flex items-center gap-2 bg-red-50 text-red-600 px-4 py-2 rounded-lg hover:bg-red-100 transition-colors font-medium"
          >
            <FiLogOut /> Logout
          </button>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 py-8">
        {/* Welcome Section */}
        <div className="bg-gradient-to-r from-blue-600 via-blue-700 to-purple-700 text-white rounded-2xl p-8 mb-8 shadow-xl animate-fade-in relative overflow-hidden">
          <div className="absolute inset-0 overflow-hidden pointer-events-none">
            <div className="absolute -top-20 -right-20 w-60 h-60 bg-white/10 rounded-full blur-2xl" />
            <div className="absolute -bottom-20 -left-20 w-60 h-60 bg-purple-500/20 rounded-full blur-2xl" />
          </div>
          <div className="relative">
            <h2 className="text-3xl font-bold mb-2">Welcome back! ðŸ‘‹</h2>
            <p className="text-blue-100 text-lg">
              Continue your learning journey and master core CS concepts
            </p>
          </div>
        </div>

        {/* Action Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <div
            onClick={navigateTo('/courses')}
            className="bg-white rounded-lg shadow p-6 cursor-pointer hover:shadow-lg transition-all group"
          >
            <div className="w-14 h-14 bg-blue-100 rounded-xl flex items-center justify-center mb-4 group-hover:bg-blue-600 transition-colors">
              <FiBook className="text-2xl text-blue-600 group-hover:text-white transition-colors" />
            </div>
            <h3 className="text-xl font-bold mb-2 text-gray-900">Courses</h3>
            <p className="text-gray-600">
              Explore DSA, DBMS, Algorithms, and OOP courses
            </p>
          </div>

          <div
            onClick={navigateTo('/chat/dsa')}
            className="bg-white rounded-lg shadow p-6 cursor-pointer hover:shadow-lg transition-all group"
          >
            <div className="w-14 h-14 bg-green-100 rounded-xl flex items-center justify-center mb-4 group-hover:bg-green-600 transition-colors">
              <FiMessageSquare className="text-2xl text-green-600 group-hover:text-white transition-colors" />
            </div>
            <h3 className="text-xl font-bold mb-2 text-gray-900">Chat with AI</h3>
            <p className="text-gray-600">
              Ask questions and get personalized explanations
            </p>
          </div>
        </div>

        {/* Personalized Insights */}
        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
            <FiZap className="text-yellow-500" /> Your Personalized Insights
          </h3>
          <PersonalizedInsights />
        </div>
      </main>
    </div>
  );
});

export default DashboardPage;
