import React, { useRef, useState } from 'react';
import { FiCpu, FiCopy, FiCheck } from 'react-icons/fi';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

interface AIResponseBoxProps {
  content: string;
}

const CopyButton: React.FC<{ text: string }> = ({ text }) => {
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  return (
    <button
      onClick={handleCopy}
      className={`p-2 rounded-lg transition-all ${copied ? 'bg-green-100 text-green-600' : 'bg-gray-100 hover:bg-gray-200 text-gray-500 hover:text-gray-700'}`}
      title={copied ? 'Copied!' : 'Copy'}
    >
      {copied ? <FiCheck size={16} /> : <FiCopy size={16} />}
    </button>
  );
};

const CodeBlock: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const codeRef = useRef<HTMLPreElement>(null);
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    const text = codeRef.current?.textContent || '';
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  return (
    <div className="my-4 rounded-xl overflow-hidden border border-gray-200 shadow-lg hover:shadow-xl transition-shadow bg-white">
      <div className="flex items-center justify-between px-5 py-3 bg-gradient-to-r from-slate-700 to-slate-800 border-b border-slate-600">
        <div className="flex items-center gap-2">
          <div className="w-6 h-6 bg-gradient-to-br from-blue-400 to-purple-500 rounded-md flex items-center justify-center">
            <span className="text-white text-xs font-bold">&lt;/&gt;</span>
          </div>
          <span className="text-slate-200 text-xs font-mono font-semibold uppercase tracking-widest">Code</span>
        </div>
        <button
          onClick={handleCopy}
          className={`flex items-center gap-1.5 px-3 py-1.5 rounded-lg text-xs font-medium transition-all ${
            copied 
              ? 'bg-green-500/20 text-green-300 border border-green-500/30' 
              : 'bg-slate-600/50 hover:bg-slate-600 text-slate-300 hover:text-slate-100 border border-slate-500/30'
          }`}
        >
          {copied ? (
            <>
              <FiCheck size={13} /> Copied
            </>
          ) : (
            <>
              <FiCopy size={13} /> Copy
            </>
          )}
        </button>
      </div>
      <div className="bg-gradient-to-b from-slate-900 to-slate-950 p-4 overflow-x-auto">
        <pre ref={codeRef} className="m-0">
          <code className="text-sm text-slate-100 font-mono leading-relaxed block whitespace-pre-wrap break-words">{children}</code>
        </pre>
      </div>
    </div>
  );
};

export const AIResponseBox: React.FC<AIResponseBoxProps> = ({ content }) => {
  return (
    <div className="flex justify-start">
      <div className="flex gap-3 max-w-[92%]">
        {/* AI Avatar */}
        <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-blue-600 rounded-xl flex items-center justify-center flex-shrink-0 mt-1 shadow-lg">
          <FiCpu className="text-white" size={20} />
        </div>

        {/* Response Container */}
        <div className="relative group bg-white border border-gray-200 rounded-2xl rounded-bl-md px-6 py-5 shadow-lg overflow-hidden">
          {/* Top accent bar */}
          <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500"></div>

          {/* Copy button */}
          <div className="absolute right-4 top-4 opacity-0 group-hover:opacity-100 transition-opacity z-10">
            <CopyButton text={content} />
          </div>

          {/* Content */}
          <div className="prose prose-sm max-w-none text-gray-700">
            <ReactMarkdown
              remarkPlugins={[remarkGfm]}
              components={{
                h1: ({ children }) => (
                  <h1 className="text-xl font-bold text-gray-900 mt-4 mb-3 first:mt-0 pb-2 border-b-2 border-blue-500 flex items-center gap-2">
                    <span>ðŸ“š</span> {children}
                  </h1>
                ),
                h2: ({ children }) => (
                  <h2 className="text-lg font-bold text-gray-800 mt-6 mb-3 first:mt-0 flex items-center gap-2 bg-gradient-to-r from-blue-50 to-transparent px-3 py-2 rounded-lg border-l-4 border-blue-500">
                    {children}
                  </h2>
                ),
                h3: ({ children }) => (
                  <h3 className="text-base font-semibold text-gray-800 mt-4 mb-2 flex items-center gap-2">
                    <span className="w-2 h-2 bg-blue-500 rounded-full"></span> {children}
                  </h3>
                ),
                p: ({ children }) => <p className="text-gray-700 my-3 leading-relaxed text-[15px]">{children}</p>,
                strong: ({ children }) => <strong className="font-bold text-gray-900 bg-yellow-100 px-1 rounded">{children}</strong>,
                pre: ({ children }) => <CodeBlock>{children}</CodeBlock>,
                code: ({ className, children, ...props }) => {
                  const isInline = !className;
                  return isInline ? (
                    <code className="bg-gradient-to-r from-blue-50 to-indigo-50 text-blue-700 px-2 py-0.5 rounded-md text-sm font-mono border border-blue-200 shadow-sm" {...props}>
                      {children}
                    </code>
                  ) : (
