import React, { useEffect, useState, useCallback, memo } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiArrowLeft, FiMessageSquare, FiBookOpen, FiAward } from 'react-icons/fi';
import { coursesAPI } from '../services/api';

interface CoursesPageProps {
  onLogout: () => void;
}

interface Course {
  id: string;
  name: string;
  description: string;
  topics: string[];
  difficulty: string;
}

// Skeleton loader component
const Skeleton = ({ className = '' }: { className?: string }) => (
  <div className={`bg-gray-200 animate-pulse rounded ${className}`} />
);

const difficultyColors: Record<string, string> = {
  Beginner: 'bg-green-100 text-green-800',
  Intermediate: 'bg-blue-100 text-blue-800',
  Advanced: 'bg-purple-100 text-purple-800',
};

const CourseCard = memo(({ course, onStart, index }: { course: Course; onStart: (id: string) => void; index: number }) => (
  <div className="bg-white rounded-lg shadow p-6 hover:shadow-lg transition-all">
    <div className="flex items-start justify-between mb-4">
      <div className="flex items-center gap-3">
        <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-xl flex items-center justify-center">
          <FiBookOpen className="text-white text-xl" />
        </div>
        <div>
          <h3 className="text-xl font-bold text-gray-900">{course.name}</h3>
          <span className={`inline-block mt-1 px-3 py-0.5 text-xs font-semibold rounded-full ${difficultyColors[course.difficulty] || 'bg-gray-100 text-gray-800'}`}>
            {course.difficulty}
          </span>
        </div>
      </div>
    </div>

    <p className="text-gray-600 mb-4">{course.description}</p>

    <div className="mb-5">
      <h4 className="font-semibold text-gray-900 mb-2 text-sm">Topics covered:</h4>
      <div className="flex flex-wrap gap-2">
        {course.topics.slice(0, 5).map((topic, idx) => (
          <span
            key={idx}
            className="px-2.5 py-1 bg-gray-100 text-gray-700 text-xs rounded-lg font-medium"
          >
            {topic}
          </span>
        ))}
        {course.topics.length > 5 && (
          <span className="px-2.5 py-1 bg-gray-100 text-gray-500 text-xs rounded-lg">
            +{course.topics.length - 5} more
          </span>
        )}
      </div>
    </div>

    <button
      onClick={() => onStart(course.id)}
      className="w-full bg-gradient-to-r from-blue-600 to-purple-600 text-white py-3 rounded-xl font-semibold hover:from-blue-700 hover:to-purple-700 transition-all flex items-center justify-center gap-2 shadow-sm"
    >
      <FiMessageSquare /> Start Learning
    </button>
  </div>
));

const CoursesPage: React.FC<CoursesPageProps> = memo(({ onLogout }) => {
  const navigate = useNavigate();
  const [courses, setCourses] = useState<Course[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;

    const fetchCourses = async () => {
      try {
        const data = await coursesAPI.getAll();
        if (mounted) setCourses(data.courses);
      } catch (error) {
        console.error('Failed to fetch courses:', error);
      } finally {
        if (mounted) setLoading(false);
      }
    };

    fetchCourses();
    return () => { mounted = false; };
  }, []);

  const handleStartLearning = useCallback((courseId: string) => {
    navigate(`/chat/${courseId}`);
  }, [navigate]);

  const goBack = useCallback(() => navigate('/dashboard'), [navigate]);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 py-4 flex items-center gap-4">
          <button
            onClick={goBack}
            className="flex items-center gap-2 text-blue-600 hover:text-blue-700 transition-colors"
          >
            <FiArrowLeft /> Back
          </button>
          <h1 className="text-2xl font-bold text-gray-900">Courses</h1>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 py-8">
        {/* Hero Section */}
        <div className="bg-gradient-to-r from-blue-600 via-blue-700 to-purple-700 text-white rounded-2xl p-8 mb-8 shadow-xl">
          <div className="flex items-center gap-4 mb-4">
            <FiAward className="text-4xl" />
            <h2 className="text-3xl font-bold">Master Core CS Concepts</h2>
          </div>
          <p className="text-blue-100 text-lg max-w-2xl">
            Choose a course below and start your AI-powered learning journey. Get personalized explanations, practice problems, and track your progress.
          </p>
        </div>

        {loading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {[1, 2, 3, 4].map((i) => (
              <div key={i} className="bg-white rounded-lg shadow p-6">
                <div className="flex items-center gap-3 mb-4">
                  <Skeleton className="w-12 h-12 rounded-xl" />
                  <div className="flex-1">
                    <Skeleton className="h-6 w-48 mb-2" />
                    <Skeleton className="h-5 w-24" />
                  </div>
                </div>
                <Skeleton className="h-16 w-full mb-4" />
                <div className="flex gap-2 mb-5">
                  <Skeleton className="h-6 w-16" />
                  <Skeleton className="h-6 w-20" />
                  <Skeleton className="h-6 w-14" />
                </div>
                <Skeleton className="h-12 w-full rounded-xl" />
              </div>
            ))}
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {courses.map((course, index) => (
              <CourseCard
                key={course.id}
                course={course}
                onStart={handleStartLearning}
                index={index}
              />
            ))}
          </div>
        )}

        {!loading && courses.length === 0 && (
          <div className="text-center py-12 text-gray-500">
            No courses available at the moment.
          </div>
        )}
      </main>
    </div>
  );
});

export default CoursesPage;
