import React from 'react';
import { FiCheckCircle, FiClock, FiTrendingUp } from 'react-icons/fi';

interface Insight {
  icon: React.ReactNode;
  title: string;
  description: string;
  color: string;
}

export const PersonalizedInsights: React.FC = () => {
  const insights: Insight[] = [
    {
      icon: <FiTrendingUp className="text-lg" />,
      title: 'Focus on trees (lowest mastery)',
      description: 'Trees are your weakest area. Dedicate more time to mastering tree concepts.',
      color: 'bg-orange-50 border-orange-200'
    },
    {
      icon: <FiClock className="text-lg" />,
      title: 'You learn best at 9-11 AM (peak focus)',
      description: 'Your focus score is highest during morning hours. Schedule important learning then.',
      color: 'bg-green-50 border-green-200'
    },
    {
      icon: <FiCheckCircle className="text-lg" />,
      title: '30 min sessions > 2 hour sessions (for you)',
      description: 'Shorter, focused sessions work better for your learning style. Take regular breaks.',
      color: 'bg-blue-50 border-blue-200'
    }
  ];

  return (
    <div className="space-y-3">
      {insights.map((insight, idx) => (
        <div key={idx} className={`${insight.color} border rounded-lg p-4 flex items-start gap-3`}>
          <div className="flex-shrink-0 mt-0.5 text-gray-700">
            {insight.icon}
          </div>
          <div className="flex-1">
            <div className="font-semibold text-gray-900 text-sm">{insight.title}</div>
            <div className="text-gray-600 text-xs mt-1">{insight.description}</div>
          </div>
          <div className="flex-shrink-0">
            <FiCheckCircle className="text-green-600" size={18} />
          </div>
        </div>
      ))}
    </div>
  );
};
