import React from 'react';
import { FiTarget, FiClock, FiZap, FiTrendingUp, FiAward, FiSun } from 'react-icons/fi';

interface Insight {
  icon: React.ReactNode;
  iconBg: string;
  title: string;
  description: string;
  color: string;
  metric?: {
    value: string;
    label: string;
    progress?: number;
  };
  actionLabel?: string;
}

export const PersonalizedInsights: React.FC = () => {
  const insights: Insight[] = [
    {
      icon: <FiTarget className="text-xl" />,
      iconBg: 'bg-orange-500',
      title: 'Focus on Trees (Lowest Mastery)',
      description: 'Trees are your weakest area. Dedicate more time to mastering tree concepts like binary trees, BST, and traversals.',
      color: 'bg-gradient-to-r from-orange-50 to-amber-50 border-orange-200',
      metric: {
        value: '35%',
        label: 'Tree Mastery',
        progress: 35
      },
      actionLabel: 'Practice Trees →'
    },
    {
      icon: <FiSun className="text-xl" />,
      iconBg: 'bg-green-500',
      title: 'Peak Focus: 9-11 AM',
      description: 'Your focus score is highest during morning hours. Schedule important learning sessions then for maximum retention.',
      color: 'bg-gradient-to-r from-green-50 to-emerald-50 border-green-200',
      metric: {
        value: '9-11 AM',
        label: 'Best Study Time',
      },
      actionLabel: 'Set Reminder →'
    },
    {
      icon: <FiClock className="text-xl" />,
      iconBg: 'bg-blue-500',
      title: '30 min Sessions Work Best',
      description: 'Shorter, focused sessions work better for your learning style. Take 5-minute breaks between sessions.',
      color: 'bg-gradient-to-r from-blue-50 to-indigo-50 border-blue-200',
      metric: {
        value: '30 min',
        label: 'Optimal Duration',
      },
      actionLabel: 'Start Timer →'
    }
  ];

  // Calculate overall learning score
  const learningScore = 72;

  return (
    <div className="space-y-6">
      {/* Learning Score Overview */}
      <div className="bg-gradient-to-r from-purple-600 to-indigo-600 rounded-xl p-5 text-white">
        <div className="flex items-center justify-between">
          <div>
            <div className="flex items-center gap-2 mb-1">
              <FiAward className="text-yellow-300" />
              <span className="text-purple-200 text-sm font-medium">Your Learning Score</span>
            </div>
            <div className="text-4xl font-bold">{learningScore}/100</div>
            <div className="flex items-center gap-1 mt-1 text-green-300 text-sm">
              <FiTrendingUp />
              <span>+5 points this week</span>
            </div>
          </div>
          <div className="relative w-20 h-20">
            <svg className="w-20 h-20 transform -rotate-90">
              <circle cx="40" cy="40" r="35" stroke="rgba(255,255,255,0.2)" strokeWidth="6" fill="none" />
              <circle 
                cx="40" cy="40" r="35" 
                stroke="white" 
                strokeWidth="6" 
                fill="none"
                strokeDasharray={`${learningScore * 2.2} 220`}
                strokeLinecap="round"
              />
            </svg>
            <div className="absolute inset-0 flex items-center justify-center">
              <FiZap className="text-yellow-300 text-2xl" />
            </div>
          </div>
        </div>
      </div>

      {/* Personalized Insights Cards */}
      <div className="grid gap-4">
        {insights.map((insight, idx) => (
          <div 
            key={idx} 
            className={`${insight.color} border rounded-xl p-4 hover:shadow-md transition-all duration-200 cursor-pointer group`}
          >
            <div className="flex items-start gap-4">
              {/* Icon */}
              <div className={`${insight.iconBg} w-12 h-12 rounded-xl flex items-center justify-center text-white flex-shrink-0 shadow-lg group-hover:scale-110 transition-transform`}>
                {insight.icon}
              </div>

              {/* Content */}
              <div className="flex-1 min-w-0">
                <div className="flex items-start justify-between gap-2">
                  <h4 className="font-bold text-gray-900">{insight.title}</h4>
                  {insight.metric && (
                    <div className="text-right flex-shrink-0">
                      <div className="text-lg font-bold text-gray-900">{insight.metric.value}</div>
                      <div className="text-xs text-gray-500">{insight.metric.label}</div>
                    </div>
                  )}
                </div>
                <p className="text-gray-600 text-sm mt-1">{insight.description}</p>

                {/* Progress Bar (if applicable) */}
                {insight.metric?.progress !== undefined && (
                  <div className="mt-3">
                    <div className="h-2 bg-gray-200 rounded-full overflow-hidden">
                      <div 
                        className="h-full bg-gradient-to-r from-orange-400 to-orange-600 rounded-full transition-all duration-500"
                        style={{ width: `${insight.metric.progress}%` }}
                      />
                    </div>
                    <div className="flex justify-between text-xs text-gray-500 mt-1">
                      <span>Current</span>
                      <span>Goal: 80%</span>
                    </div>
                  </div>
                )}

                {/* Action Button */}
                {insight.actionLabel && (
                  <button className="mt-3 text-sm font-medium text-blue-600 hover:text-blue-800 transition-colors group-hover:underline">
                    {insight.actionLabel}
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>


    </div>
  );
};
