import React, { useCallback, memo, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiBook, FiZap, FiArrowRight, FiTrendingUp } from 'react-icons/fi';
import { PersonalizedInsights } from '../components/PersonalizedInsights';
import { analyticsAPI } from '../services/api';

interface MasteryTopic {
  topic: string;
  mastery: number;
  sessionsCount: number;
}

const DashboardPage: React.FC = memo(() => {
  const navigate = useNavigate();
  const [masteryTopics, setMasteryTopics] = useState<MasteryTopic[]>([]);
  const [loadingTopics, setLoadingTopics] = useState(true);

  useEffect(() => {
    const fetchTopics = async () => {
      try {
        const response = await analyticsAPI.getInsights();
        setMasteryTopics(response.data.masteryByTopic || []);
      } catch (error) {
        console.error('Failed to fetch topics:', error);
      } finally {
        setLoadingTopics(false);
      }
    };
    fetchTopics();
  }, []);

  const navigateTo = useCallback((path: string) => () => navigate(path), [navigate]);

  const navigateToTopic = useCallback((topic: string) => () => {
    navigate(`/chat/dsa?topic=${topic.toLowerCase()}`);
  }, [navigate]);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl flex items-center justify-center">
              <span className="text-white font-bold">LC</span>
            </div>
            <h1 className="text-2xl font-bold text-gray-900">Learning Copilot</h1>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 py-8">
        {/* Welcome Section */}
        <div className="bg-gradient-to-r from-blue-600 via-blue-700 to-purple-700 text-white rounded-2xl p-8 mb-8 shadow-xl animate-fade-in relative overflow-hidden">
          <div className="absolute inset-0 overflow-hidden pointer-events-none">
            <div className="absolute -top-20 -right-20 w-60 h-60 bg-white/10 rounded-full blur-2xl" />
            <div className="absolute -bottom-20 -left-20 w-60 h-60 bg-purple-500/20 rounded-full blur-2xl" />
          </div>
          <div className="relative">
            <h2 className="text-3xl font-bold mb-2">Welcome back! ðŸ‘‹</h2>
            <p className="text-blue-100 text-lg">
              Continue your learning journey and master core CS concepts
            </p>
          </div>
        </div>

        {/* Continue Learning Section */}
        <div className="mb-8">
          <div className="flex items-center gap-2 mb-4">
            <FiTrendingUp className="text-blue-600" />
            <h2 className="text-xl font-bold text-gray-900">Continue Your Learning Journey</h2>
          </div>

          {loadingTopics ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {[1, 2, 3].map(i => (
                <div key={i} className="bg-gray-200 animate-pulse rounded-lg h-32" />
              ))}
            </div>
          ) : masteryTopics.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {masteryTopics.slice(0, 6).map((topic, idx) => {
                const progressColor = topic.mastery < 40 ? 'bg-red-100 border-red-200' : topic.mastery < 70 ? 'bg-yellow-100 border-yellow-200' : 'bg-green-100 border-green-200';
                const progressBarColor = topic.mastery < 40 ? 'bg-red-500' : topic.mastery < 70 ? 'bg-yellow-500' : 'bg-green-500';

                return (
                  <div
                    key={idx}
                    className={`${progressColor} border rounded-lg p-4 hover:shadow-md transition-all cursor-pointer group`}
                    onClick={navigateToTopic(topic.topic)}
                  >
                    <div className="flex justify-between items-start mb-3">
                      <h3 className="font-semibold text-gray-900 capitalize flex-1">{topic.topic}</h3>
                      <span className="text-sm font-bold text-gray-700 ml-2">{topic.mastery}%</span>
                    </div>
                    <div className="w-full bg-gray-300 rounded-full h-2 mb-2">
                      <div
                        className={`${progressBarColor} h-2 rounded-full transition-all`}
                        style={{ width: `${topic.mastery}%` }}
                      />
                    </div>
                    <div className="flex items-center justify-between text-xs text-gray-600">
                      <span>{topic.sessionsCount} sessions</span>
                      <FiArrowRight className="group-hover:translate-x-1 transition-transform" />
                    </div>
                  </div>
                );
              })}
            </div>
          ) : null}
        </div>

        {/* Action Cards */}
        <div className="grid grid-cols-1 gap-6 mb-8">
          <div
            onClick={navigateTo('/courses')}
            className="bg-white rounded-lg shadow p-6 cursor-pointer hover:shadow-lg transition-all group"
          >
            <div className="w-14 h-14 bg-blue-100 rounded-xl flex items-center justify-center mb-4 group-hover:bg-blue-600 transition-colors">
              <FiBook className="text-2xl text-blue-600 group-hover:text-white transition-colors" />
            </div>
            <h3 className="text-xl font-bold mb-2 text-gray-900">Explore All Courses</h3>
            <p className="text-gray-600">
              Browse 35+ CS and ECE courses
            </p>
          </div>
        </div>

        {/* Personalized Insights */}
        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
            <FiZap className="text-yellow-500" /> Your Personalized Insights
          </h3>
          <PersonalizedInsights />
        </div>
      </main>
    </div>
  );
});

export default DashboardPage;
