import React, { useState, useEffect, useRef, useCallback, memo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FiSend, FiArrowLeft, FiCpu, FiCopy, FiCheck, FiAlertCircle, FiInfo, FiBookOpen, FiCode, FiList, FiCheckCircle } from 'react-icons/fi';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import { chatAPI } from '../services/api';

// Code block with copy functionality
const CodeBlock = memo(({ children, className }: { children: React.ReactNode; className?: string }) => {
  const [copied, setCopied] = useState(false);
  const codeRef = useRef<HTMLPreElement>(null);

  const language = className?.replace('language-', '') || 'code';

  const handleCopy = async () => {
    const code = codeRef.current?.textContent || '';
    await navigator.clipboard.writeText(code);
    setCopied(true);
    setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div className="relative group my-4 rounded-xl overflow-hidden shadow-sm border border-gray-200">
      {/* Code header */}
      <div className="flex items-center justify-between bg-gray-800 px-4 py-2 border-b border-gray-700">
        <div className="flex items-center gap-2">
          <FiCode className="text-gray-400" size={14} />
          <span className="text-xs font-medium text-gray-400 uppercase tracking-wide">{language}</span>
        </div>
        <button
          onClick={handleCopy}
          className="flex items-center gap-1.5 text-xs text-gray-400 hover:text-white transition-colors px-2 py-1 rounded hover:bg-gray-700"
        >
          {copied ? (
            <>
              <FiCheck size={14} className="text-green-400" />
              <span className="text-green-400">Copied!</span>
            </>
          ) : (
            <>
              <FiCopy size={14} />
              <span>Copy</span>
            </>
          )}
        </button>
      </div>
      {/* Code content */}
      <pre ref={codeRef} className="bg-gray-900 text-gray-100 p-4 overflow-x-auto text-sm leading-relaxed">
        {children}
      </pre>
    </div>
  );
});

// Callout/Alert component for important information
const Callout = ({ type, children }: { type: 'info' | 'warning' | 'tip' | 'note'; children: React.ReactNode }) => {
  const styles = {
    info: {
      bg: 'bg-blue-50',
      border: 'border-blue-200',
      icon: <FiInfo className="text-blue-500" size={18} />,
      title: 'Info',
      titleColor: 'text-blue-700'
    },
    warning: {
      bg: 'bg-amber-50',
      border: 'border-amber-200',
      icon: <FiAlertCircle className="text-amber-500" size={18} />,
      title: 'Warning',
      titleColor: 'text-amber-700'
    },
    tip: {
      bg: 'bg-green-50',
      border: 'border-green-200',
      icon: <FiCheckCircle className="text-green-500" size={18} />,
      title: 'Tip',
      titleColor: 'text-green-700'
    },
    note: {
      bg: 'bg-purple-50',
      border: 'border-purple-200',
      icon: <FiBookOpen className="text-purple-500" size={18} />,
      title: 'Note',
      titleColor: 'text-purple-700'
    }
  };

  const style = styles[type];

  return (
    <div className={`${style.bg} ${style.border} border rounded-xl p-4 my-4`}>
      <div className="flex items-start gap-3">
        <div className="flex-shrink-0 mt-0.5">{style.icon}</div>
        <div className="flex-1">
          <span className={`font-semibold ${style.titleColor} text-sm`}>{style.title}</span>
          <div className="text-gray-700 text-sm mt-1">{children}</div>
        </div>
      </div>
    </div>
  );
};

// Typing indicator component
const TypingIndicator = () => (
  <div className="flex items-center gap-1 p-3">
    <span className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0ms' }} />
    <span className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '150ms' }} />
    <span className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '300ms' }} />
  </div>
);

interface ChatPageProps {
  onLogout: () => void;
}

interface Message {
  id: string;
  userMessage: string;
  aiResponse: string;
  timestamp: Date;
}

const ChatPage: React.FC<ChatPageProps> = memo(({ onLogout }) => {
  const { courseId } = useParams<{ courseId: string }>();
  const navigate = useNavigate();
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [conversationId, setConversationId] = useState<string | undefined>(undefined);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, scrollToBottom]);

  // Focus input on mount
  useEffect(() => {
    inputRef.current?.focus();
  }, []);

  const handleSendMessage = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    const trimmedInput = input.trim();
    if (!trimmedInput || loading) return;

    setLoading(true);
    setInput('');

    try {
      const response = await chatAPI.sendMessage(courseId || 'dsa', trimmedInput, conversationId);

      // Store the conversationId for subsequent messages to maintain context
      if (!conversationId && response.data.conversationId) {
        setConversationId(response.data.conversationId);
      }

      const newMessage: Message = {
        id: `msg_${Date.now()}`,
        userMessage: trimmedInput,
        aiResponse: response.data.aiResponse,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, newMessage]);
    } catch (error) {
      console.error('Failed to send message:', error);
      // Show error in chat
      setMessages(prev => [...prev, {
        id: `error_${Date.now()}`,
        userMessage: trimmedInput,
        aiResponse: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date(),
      }]);
    } finally {
      setLoading(false);
      inputRef.current?.focus();
    }
  }, [input, loading, courseId, conversationId]);

  const handleKeyDown = useCallback((e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage(e);
    }
  }, [handleSendMessage]);

  const goBack = useCallback(() => navigate('/dashboard'), [navigate]);

  const courseName = courseId?.toUpperCase() || 'Learning';

  return (
    <div className="h-screen flex flex-col bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm flex-shrink-0">
        <div className="max-w-4xl mx-auto px-4 py-3 flex items-center gap-4">
          <button
            onClick={goBack}
            className="flex items-center gap-2 text-blue-600 hover:text-blue-700 transition-colors"
          >
            <FiArrowLeft size={20} /> Back
          </button>
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl flex items-center justify-center">
              <FiCpu className="text-white" />
            </div>
            <div>
              <h1 className="text-lg font-bold text-gray-900">{courseName} Assistant</h1>
              <p className="text-xs text-gray-500">AI-powered learning companion</p>
            </div>
          </div>
        </div>
      </header>

      {/* Messages Container */}
      <div className="flex-1 overflow-y-auto">
        <div className="max-w-4xl mx-auto px-4 py-6 space-y-6">
          {messages.length === 0 ? (
            <div className="flex flex-col items-center justify-center h-full min-h-[400px] text-center animate-fade-in">
              <div className="w-20 h-20 bg-gradient-to-br from-blue-100 to-purple-100 rounded-2xl flex items-center justify-center mb-6">
                <FiCpu className="text-4xl text-blue-600" />
              </div>
              <h2 className="text-2xl font-bold text-gray-900 mb-3">
                Start Learning {courseName}!
              </h2>
              <p className="text-gray-600 max-w-md mb-8">
                Ask any question about {courseName} and I'll help you understand it with personalized explanations.
              </p>

              {/* Quick prompts */}
              <div className="flex flex-wrap gap-2 justify-center max-w-lg">
                {[
                  `What is ${courseName}?`,
                  'Explain with examples',
                  'Best practices',
                  'Common mistakes'
                ].map((prompt, idx) => (
                  <button
                    key={idx}
                    onClick={() => setInput(prompt)}
                    className="px-4 py-2 bg-white border border-gray-200 rounded-full text-sm text-gray-700 hover:bg-blue-50 hover:border-blue-300 hover:text-blue-600 transition-all"
                  >
                    {prompt}
                  </button>
                ))}
              </div>
            </div>
          ) : (
            messages.map((msg, idx) => (
              <div key={msg.id} className="space-y-4 animate-fade-in" style={{ animationDelay: `${idx * 50}ms` }}>
                {/* User Message */}
                <div className="flex justify-end">
                  <div className="bg-gradient-to-br from-blue-600 to-blue-700 text-white rounded-2xl rounded-br-md px-4 py-3 max-w-[80%] shadow-sm">
                    {msg.userMessage}
                  </div>
                </div>

                {/* AI Response */}
                <div className="flex justify-start">
                  <div className="flex gap-3 max-w-[85%]">
                    <div className="w-8 h-8 bg-gradient-to-br from-purple-100 to-blue-100 rounded-lg flex items-center justify-center flex-shrink-0">
                      <FiCpu className="text-purple-600" size={16} />
                    </div>
                    <div className="bg-white border border-gray-100 rounded-2xl rounded-bl-md px-5 py-4 shadow-sm">
                      <ReactMarkdown
                        remarkPlugins={[remarkGfm]}
                        components={{
                          // Code blocks with copy functionality
                          pre: ({ children }) => <>{children}</>,
                          code: ({ className, children, ...props }) => {
                            const isInline = !className && typeof children === 'string' && !children.includes('\n');
                            if (isInline) {
                              return (
                                <code className="bg-gradient-to-r from-pink-50 to-purple-50 text-pink-600 px-1.5 py-0.5 rounded-md text-sm font-mono border border-pink-100" {...props}>
                                  {children}
                                </code>
                              );
                            }
                            return (
                              <CodeBlock className={className}>
                                <code className={className} {...props}>{children}</code>
                              </CodeBlock>
                            );
                          },
                          // Enhanced tables
                          table: ({ children }) => (
                            <div className="overflow-x-auto my-4 rounded-xl border border-gray-200 shadow-sm">
                              <table className="w-full text-sm">
                                {children}
                              </table>
                            </div>
                          ),
                          thead: ({ children }) => (
                            <thead className="bg-gradient-to-r from-gray-50 to-gray-100">
                              {children}
                            </thead>
                          ),
                          th: ({ children }) => (
                            <th className="px-4 py-3 text-left font-semibold text-gray-700 border-b border-gray-200">
                              {children}
                            </th>
                          ),
                          td: ({ children }) => (
                            <td className="px-4 py-3 border-b border-gray-100 text-gray-600">{children}</td>
                          ),
                          tr: ({ children }) => (
                            <tr className="hover:bg-gray-50 transition-colors">{children}</tr>
                          ),
                          // Enhanced lists
                          ul: ({ children }) => (
                            <ul className="my-3 space-y-2 ml-1">{children}</ul>
                          ),
                          ol: ({ children }) => (
                            <ol className="my-3 space-y-2 ml-1 list-none counter-reset-item">{children}</ol>
                          ),
                          li: ({ children, ...props }) => {
                            const isOrdered = props.node?.position?.start?.column === 1;
                            return (
                              <li className="flex items-start gap-2 text-gray-700">
                                <span className="flex-shrink-0 w-5 h-5 rounded-full bg-gradient-to-br from-blue-100 to-purple-100 flex items-center justify-center mt-0.5">
                                  <span className="w-1.5 h-1.5 rounded-full bg-blue-500"></span>
                                </span>
                                <span className="flex-1">{children}</span>
                              </li>
                            );
                          },
                          // Enhanced headings
                          h1: ({ children }) => (
                            <h1 className="text-xl font-bold mt-6 mb-3 text-gray-900 flex items-center gap-2 pb-2 border-b border-gray-100">
                              <span className="w-1 h-6 bg-gradient-to-b from-blue-500 to-purple-500 rounded-full"></span>
                              {children}
                            </h1>
                          ),
                          h2: ({ children }) => (
                            <h2 className="text-lg font-bold mt-5 mb-2 text-gray-800 flex items-center gap-2">
                              <span className="w-1 h-5 bg-gradient-to-b from-blue-400 to-purple-400 rounded-full"></span>
                              {children}
                            </h2>
                          ),
                          h3: ({ children }) => (
                            <h3 className="text-base font-semibold mt-4 mb-2 text-gray-700 flex items-center gap-2">
                              <span className="w-1 h-4 bg-blue-300 rounded-full"></span>
                              {children}
                            </h3>
                          ),
                          h4: ({ children }) => (
                            <h4 className="text-sm font-semibold mt-3 mb-1 text-gray-600">{children}</h4>
                          ),
                          // Enhanced paragraphs
                          p: ({ children }) => (
                            <p className="my-2 text-gray-700 leading-relaxed">{children}</p>
                          ),
                          // Enhanced text styling
                          strong: ({ children }) => (
                            <strong className="font-semibold text-gray-900 bg-yellow-50 px-0.5 rounded">{children}</strong>
                          ),
                          em: ({ children }) => (
                            <em className="italic text-gray-600">{children}</em>
                          ),
                          // Enhanced blockquotes
                          blockquote: ({ children }) => (
                            <blockquote className="my-4 pl-4 border-l-4 border-gradient-to-b from-blue-400 to-purple-400 bg-gradient-to-r from-blue-50 to-transparent py-3 pr-4 rounded-r-lg italic text-gray-600">
                              {children}
                            </blockquote>
                          ),
                          // Enhanced links
                          a: ({ href, children }) => (
                            <a 
                              href={href} 
                              target="_blank" 
                              rel="noopener noreferrer"
                              className="text-blue-600 hover:text-blue-700 underline decoration-blue-200 hover:decoration-blue-400 transition-colors font-medium"
                            >
                              {children}
                            </a>
                          ),
                          // Horizontal rule
                          hr: () => (
                            <hr className="my-6 border-0 h-px bg-gradient-to-r from-transparent via-gray-300 to-transparent" />
                          ),
                        }}
                      >
                        {msg.aiResponse}
                      </ReactMarkdown>
                    </div>
                  </div>
                </div>
              </div>
            ))
          )}

          {/* Typing indicator */}
          {loading && (
            <div className="flex justify-start animate-fade-in">
              <div className="flex gap-3 items-center">
                <div className="w-8 h-8 bg-gradient-to-br from-purple-100 to-blue-100 rounded-lg flex items-center justify-center flex-shrink-0">
                  <FiCpu className="text-purple-600" size={16} />
                </div>
                <div className="bg-white border border-gray-100 rounded-2xl px-4 py-2 shadow-sm">
                  <TypingIndicator />
                </div>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* Input Area */}
      <div className="bg-white border-t border-gray-200 flex-shrink-0">
        <div className="max-w-4xl mx-auto px-4 py-4">
          <form onSubmit={handleSendMessage} className="flex gap-3">
            <input
              ref={inputRef}
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Ask a question..."
              className="flex-1 px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
              disabled={loading}
            />
            <button
              type="submit"
              disabled={loading || !input.trim()}
              className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-6 py-3 rounded-xl hover:from-blue-700 hover:to-purple-700 transition-all disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2 font-medium shadow-sm"
            >
              {loading ? (
                <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin" />
              ) : (
                <FiSend />
              )}
              Send
            </button>
          </form>
        </div>
      </div>
    </div>
  );
});

export default ChatPage;
