import React, { useCallback, memo, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FiBook, FiZap, FiBarChart2, FiClock, FiTarget, FiTrendingUp, FiPlay } from 'react-icons/fi';
import { PersonalizedInsights } from '../components/PersonalizedInsights';
import { analyticsAPI } from '../services/api';

interface DashboardStats {
  totalSessions: number;
  totalStudyTime: number;
  learningScore: number;
  weeklyImprovement: number;
}

const DashboardPage: React.FC = memo(() => {
  const navigate = useNavigate();
  const [stats, setStats] = useState<DashboardStats | null>(null);
  const [loadingStats, setLoadingStats] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await analyticsAPI.getInsights();
        setStats({
          totalSessions: response.data.totalSessions || 0,
          totalStudyTime: response.data.totalStudyTime || 0,
          learningScore: response.data.learningScore || 0,
          weeklyImprovement: response.data.weeklyImprovement || 0,
        });
      } catch (error) {
        console.error('Failed to fetch stats:', error);
      } finally {
        setLoadingStats(false);
      }
    };
    fetchStats();
  }, []);

  const navigateTo = useCallback((path: string) => () => navigate(path), [navigate]);

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl flex items-center justify-center">
              <span className="text-white font-bold">LC</span>
            </div>
            <h1 className="text-2xl font-bold text-gray-900">Learning Copilot</h1>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 py-8">
        {/* Welcome Section */}
        <div className="bg-gradient-to-r from-blue-600 via-blue-700 to-purple-700 text-white rounded-2xl p-8 mb-8 shadow-xl animate-fade-in relative overflow-hidden">
          <div className="absolute inset-0 overflow-hidden pointer-events-none">
            <div className="absolute -top-20 -right-20 w-60 h-60 bg-white/10 rounded-full blur-2xl" />
            <div className="absolute -bottom-20 -left-20 w-60 h-60 bg-purple-500/20 rounded-full blur-2xl" />
          </div>
          <div className="relative">
            <h2 className="text-3xl font-bold mb-2">Welcome back! ðŸ‘‹</h2>
            <p className="text-blue-100 text-lg">
              Continue your learning journey and master core CS and ECE concepts
            </p>
          </div>
        </div>

        {/* Quick Stats */}
        {!loadingStats && stats && (
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
            <div className="bg-white rounded-lg shadow p-4 border-l-4 border-blue-500">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-600 text-sm">Learning Score</p>
                  <p className="text-2xl font-bold text-gray-900">{stats.learningScore}%</p>
                </div>
                <FiBarChart2 className="text-3xl text-blue-500 opacity-20" />
              </div>
            </div>
            <div className="bg-white rounded-lg shadow p-4 border-l-4 border-green-500">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-600 text-sm">Study Sessions</p>
                  <p className="text-2xl font-bold text-gray-900">{stats.totalSessions}</p>
                </div>
                <FiClock className="text-3xl text-green-500 opacity-20" />
              </div>
            </div>
            <div className="bg-white rounded-lg shadow p-4 border-l-4 border-purple-500">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-600 text-sm">Study Time</p>
                  <p className="text-2xl font-bold text-gray-900">{Math.round(stats.totalStudyTime / 60)}h</p>
                </div>
                <FiTarget className="text-3xl text-purple-500 opacity-20" />
              </div>
            </div>
            <div className="bg-white rounded-lg shadow p-4 border-l-4 border-orange-500">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-gray-600 text-sm">Weekly Improvement</p>
                  <p className="text-2xl font-bold text-gray-900">+{stats.weeklyImprovement}%</p>
                </div>
                <FiTrendingUp className="text-3xl text-orange-500 opacity-20" />
              </div>
            </div>
          </div>
        )}

        {/* Action Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-8">
          <div
            onClick={navigateTo('/courses')}
            className="bg-white rounded-lg shadow p-6 cursor-pointer hover:shadow-lg transition-all group"
          >
            <div className="w-14 h-14 bg-blue-100 rounded-xl flex items-center justify-center mb-4 group-hover:bg-blue-600 transition-colors">
              <FiBook className="text-2xl text-blue-600 group-hover:text-white transition-colors" />
            </div>
            <h3 className="text-xl font-bold mb-2 text-gray-900">Explore Courses</h3>
            <p className="text-gray-600 text-sm">
              Browse 35+ CS and ECE courses
            </p>
          </div>
          <div
            onClick={navigateTo('/chat/dsa')}
            className="bg-white rounded-lg shadow p-6 cursor-pointer hover:shadow-lg transition-all group"
          >
            <div className="w-14 h-14 bg-purple-100 rounded-xl flex items-center justify-center mb-4 group-hover:bg-purple-600 transition-colors">
              <FiPlay className="text-2xl text-purple-600 group-hover:text-white transition-colors" />
            </div>
            <h3 className="text-xl font-bold mb-2 text-gray-900">Start Learning</h3>
            <p className="text-gray-600 text-sm">
              Chat with AI tutor now
            </p>
          </div>
        </div>

        {/* Personalized Insights */}
        <div className="bg-white rounded-lg shadow p-6 mb-8">
          <h3 className="text-xl font-bold mb-4 flex items-center gap-2">
            <FiZap className="text-yellow-500" /> Your Personalized Insights
          </h3>
          <PersonalizedInsights />
        </div>

        {/* Quick Tips */}
        <div className="bg-gradient-to-r from-indigo-50 to-blue-50 rounded-lg border border-indigo-200 p-6">
          <h3 className="text-lg font-bold text-gray-900 mb-4">ðŸ’¡ Learning Tips</h3>
          <ul className="space-y-2 text-sm text-gray-700">
            <li>âœ“ Study during your peak focus hours for better retention</li>
            <li>âœ“ Use the timer to track focused study sessions</li>
            <li>âœ“ Ask the AI tutor specific questions about topics</li>
