import React from 'react';
import { FiZap } from 'react-icons/fi';

interface FocusTimerProps {
  totalTime: number;
  activeTime: number;
  focusScore: number;
  isActive: boolean;
}

const formatTime = (seconds: number): string => {
  const mins = Math.floor(seconds / 60);
  const secs = seconds % 60;
  return `${mins}:${secs.toString().padStart(2, '0')}`;
};

const getFocusColor = (score: number): string => {
  if (score >= 80) return 'text-green-600';
  if (score >= 60) return 'text-yellow-600';
  return 'text-red-600';
};

export const FocusTimer: React.FC<FocusTimerProps> = ({ totalTime, activeTime, focusScore, isActive }) => {
  return (
    <div className="bg-white rounded-lg shadow p-4 flex items-center justify-between">
      <div className="flex items-center gap-4">
        <div className={`w-12 h-12 rounded-full flex items-center justify-center ${isActive ? 'bg-green-100' : 'bg-gray-100'}`}>
          <FiZap className={`text-xl ${isActive ? 'text-green-600' : 'text-gray-400'}`} />
        </div>
        <div>
          <div className="text-sm text-gray-600">Active Time</div>
          <div className="text-lg font-bold text-gray-900">{formatTime(activeTime)}</div>
          <div className="text-xs text-gray-500">Total: {formatTime(totalTime)}</div>
        </div>
      </div>
      <div className="text-right">
        <div className="text-sm text-gray-600">Focus Score</div>
        <div className={`text-3xl font-bold ${getFocusColor(focusScore)}`}>{focusScore}%</div>
        <div className="text-xs text-gray-500 mt-1">{isActive ? 'Focused' : 'Paused'}</div>
      </div>
    </div>
  );
};
