import React, { useRef, useState } from 'react';
import { FiCpu, FiCopy, FiCheck, FiArrowDown } from 'react-icons/fi';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

interface AIResponseBoxProps {
  content: string;
}

// Helper function to detect if content is a flowchart/diagram (not actual code)
const isFlowchartContent = (text: string): boolean => {
  const lines = text.trim().split('\n');
  // Check for arrow patterns (‚Üì, ‚Üí, ‚Üê, ‚Üë) and numbered steps
  const hasArrows = /[‚Üì‚Üí‚Üê‚Üë‚ñº‚ñ≤‚óÑ‚ñ∫]/.test(text);
  const hasNumberedSteps = /^\s*\d+\.\s+/m.test(text);
  const hasBoxChars = /[‚îå‚îê‚îî‚îò‚îÇ‚îÄ‚î¨‚î¥‚îú‚î§‚îº‚ïî‚ïó‚ïö‚ïù‚ïë‚ïê]/.test(text);
  // Check if it looks like a learning path or roadmap
  const looksLikePath = hasArrows && (hasNumberedSteps || lines.length > 3);
  // Check if it's actual code (has programming keywords/syntax)
  const hasCodeSyntax = /\b(function|const|let|var|import|export|class|def|return|if|else|for|while|=>|{|}|\(\))\b/.test(text);

  return (looksLikePath || hasBoxChars) && !hasCodeSyntax;
};

// Flowchart/Roadmap component for visual learning paths
const FlowchartBlock: React.FC<{ children: React.ReactNode; content: string }> = ({ children, content }) => {
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(content);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  // Parse flowchart content into structured items
  const parseFlowchartItems = (text: string) => {
    const lines = text.trim().split('\n').filter(line => line.trim());
    const items: { text: string; isArrow: boolean }[] = [];

    for (const line of lines) {
      const trimmed = line.trim();
      if (/^[‚Üì‚Üí‚Üê‚Üë‚ñº‚ñ≤‚óÑ‚ñ∫]+$/.test(trimmed)) {
        items.push({ text: trimmed, isArrow: true });
      } else if (trimmed) {
        items.push({ text: trimmed, isArrow: false });
      }
    }
    return items;
  };

  const items = parseFlowchartItems(content);

  return (
    <div className="my-6 rounded-2xl overflow-hidden border border-purple-200 shadow-lg bg-gradient-to-br from-purple-50 to-blue-50">
      <div className="flex items-center justify-between px-5 py-3 bg-gradient-to-r from-purple-600 to-blue-600 border-b border-purple-500">
        <div className="flex items-center gap-2">
          <div className="w-6 h-6 bg-white/20 rounded-md flex items-center justify-center">
            <span className="text-white text-sm">üó∫Ô∏è</span>
          </div>
          <span className="text-white text-xs font-semibold uppercase tracking-widest">Learning Path</span>
        </div>
        <button
          onClick={handleCopy}
          className={`flex items-center gap-1.5 px-3 py-1.5 rounded-lg text-xs font-medium transition-all ${
            copied 
              ? 'bg-green-500/30 text-green-100 border border-green-400/30' 
              : 'bg-white/10 hover:bg-white/20 text-white/90 hover:text-white border border-white/20'
          }`}
        >
          {copied ? (
            <>
              <FiCheck size={13} /> Copied
            </>
          ) : (
            <>
              <FiCopy size={13} /> Copy
            </>
          )}
        </button>
      </div>
      <div className="p-6">
        <div className="flex flex-col items-center gap-1">
          {items.map((item, index) => (
            item.isArrow ? (
              <div key={index} className="flex items-center justify-center py-1">
                <FiArrowDown className="text-purple-500 text-xl" />
              </div>
            ) : (
              <div 
                key={index} 
                className="w-full max-w-md bg-white rounded-xl px-5 py-3 shadow-md border border-purple-200 hover:border-purple-400 hover:shadow-lg transition-all cursor-default"
              >
                <span className="text-gray-800 font-medium text-center block">{item.text}</span>
              </div>
            )
          ))}
        </div>
      </div>
    </div>
  );
};

const CodeBlock: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const codeRef = useRef<HTMLPreElement>(null);
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    const text = codeRef.current?.textContent || '';
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  return (
    <div className="my-4 rounded-xl overflow-hidden border border-gray-200 shadow-lg hover:shadow-xl transition-shadow bg-white">
      <div className="flex items-center justify-between px-5 py-3 bg-gradient-to-r from-slate-700 to-slate-800 border-b border-slate-600">
        <div className="flex items-center gap-2">
          <div className="w-6 h-6 bg-gradient-to-br from-blue-400 to-purple-500 rounded-md flex items-center justify-center">
            <span className="text-white text-xs font-bold">&lt;/&gt;</span>
          </div>
          <span className="text-slate-200 text-xs font-mono font-semibold uppercase tracking-widest">Code</span>
        </div>
        <button
          onClick={handleCopy}
          className={`flex items-center gap-1.5 px-3 py-1.5 rounded-lg text-xs font-medium transition-all ${
            copied 
              ? 'bg-green-500/20 text-green-300 border border-green-500/30' 
              : 'bg-slate-600/50 hover:bg-slate-600 text-slate-300 hover:text-slate-100 border border-slate-500/30'
          }`}
        >
          {copied ? (
            <>
              <FiCheck size={13} /> Copied
            </>
          ) : (
            <>
              <FiCopy size={13} /> Copy
            </>
          )}
        </button>
      </div>
      <div className="bg-gradient-to-b from-slate-900 to-slate-950 p-4 overflow-x-auto">
        <pre ref={codeRef} className="m-0">
          <code className="text-sm text-slate-100 font-mono leading-relaxed block whitespace-pre-wrap break-words">{children}</code>
        </pre>
      </div>
    </div>
  );
};

export const AIResponseBox: React.FC<AIResponseBoxProps> = ({ content }) => {
  const [copied, setCopied] = useState(false);

  const handleCopyAll = async () => {
    try {
      await navigator.clipboard.writeText(content);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  return (
    <div className="flex justify-start">
      <div className="flex gap-3 max-w-[92%]">
        {/* AI Avatar */}
        <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-blue-600 rounded-xl flex items-center justify-center flex-shrink-0 mt-1 shadow-lg">
          <FiCpu className="text-white" size={20} />
        </div>

        {/* Response Container */}
        <div className="relative group bg-white border border-gray-200 rounded-2xl rounded-bl-md px-6 py-5 shadow-lg overflow-hidden">
          {/* Top accent bar */}
          <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500"></div>

          {/* Copy All button */}
          <div className="absolute right-4 top-4 opacity-0 group-hover:opacity-100 transition-opacity z-10">
            <button
              onClick={handleCopyAll}
              className={`flex items-center gap-1.5 px-3 py-1.5 rounded-lg text-xs font-medium transition-all ${
                copied
                  ? 'bg-green-100 text-green-600 border border-green-300'
                  : 'bg-gray-100 hover:bg-gray-200 text-gray-600 hover:text-gray-800 border border-gray-300'
              }`}
              title="Copy entire answer"
            >
              {copied ? (
                <>
                  <FiCheck size={14} /> Copied
                </>
              ) : (
                <>
                  <FiCopy size={14} /> Copy All
                </>
              )}
            </button>
          </div>

          {/* Content */}
          <div className="prose prose-sm max-w-none text-gray-700">
            <ReactMarkdown
              remarkPlugins={[remarkGfm]}
              components={{
                h1: ({ children }) => (
                  <h1 className="text-xl font-bold text-gray-900 mt-4 mb-3 first:mt-0 pb-2 border-b-2 border-blue-500 flex items-center gap-2">
                    <span>üìö</span> {children}
                  </h1>
                ),
                h2: ({ children }) => (
                  <h2 className="text-lg font-bold text-gray-800 mt-6 mb-3 first:mt-0 flex items-center gap-2 bg-gradient-to-r from-blue-50 to-transparent px-3 py-2 rounded-lg border-l-4 border-blue-500">
                    {children}
                  </h2>
                ),
                h3: ({ children }) => (
                  <h3 className="text-base font-semibold text-gray-800 mt-4 mb-2 flex items-center gap-2">
                    <span className="w-2 h-2 bg-blue-500 rounded-full"></span> {children}
                  </h3>
                ),
                p: ({ children }) => <p className="text-gray-700 my-3 leading-relaxed text-[15px]">{children}</p>,
                strong: ({ children }) => <strong className="font-bold text-gray-900 bg-yellow-100 px-1 rounded">{children}</strong>,
                pre: ({ children }) => <CodeBlock>{children}</CodeBlock>,
                code: ({ className, children, ...props }) => {
                  const isInline = !className;
                  return isInline ? (
                    <code className="bg-gradient-to-r from-blue-50 to-indigo-50 text-blue-700 px-2 py-0.5 rounded-md text-sm font-mono border border-blue-200 shadow-sm" {...props}>
                      {children}
                    </code>
                  ) : (
                    <code className={`${className || ''} text-sm`} {...props}>
                      {children}
                    </code>
                  );
                },
                table: ({ children }) => (
                  <div className="overflow-x-auto my-4 rounded-xl border border-gray-200 shadow-md">
                    <table className="min-w-full">{children}</table>
                  </div>
                ),
                thead: ({ children }) => <thead className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white">{children}</thead>,
                tbody: ({ children }) => <tbody className="bg-white divide-y divide-gray-100">{children}</tbody>,
                tr: ({ children }) => <tr className="hover:bg-blue-50 transition-colors">{children}</tr>,
                th: ({ children }) => (
                  <th className="px-5 py-3 text-left text-xs font-bold uppercase tracking-wider">{children}</th>
                ),
                td: ({ children }) => (
                  <td className="px-5 py-3 text-sm text-gray-700 border-b border-gray-100">{children}</td>
                ),
                ul: ({ children }) => <ul className="my-4 space-y-2 text-gray-700">{children}</ul>,
                ol: ({ children }) => <ol className="my-4 space-y-2 text-gray-700">{children}</ol>,
                li: ({ children }) => (
                  <li className="flex items-start gap-3 leading-relaxed pl-1">
                    <span className="flex-shrink-0 w-6 h-6 bg-blue-100 text-blue-600 rounded-full flex items-center justify-center text-xs font-bold mt-0.5">‚Ä¢</span>
                    <span className="flex-1">{children}</span>
                  </li>
                ),
                blockquote: ({ children }) => (
                  <blockquote className="my-4 p-4 bg-gradient-to-r from-amber-50 to-yellow-50 border-l-4 border-amber-400 rounded-r-xl shadow-sm">
                    <div className="flex items-start gap-3">
                      <span className="text-xl">üí°</span>
                      <div className="text-gray-700 flex-1">{children}</div>
                    </div>
                  </blockquote>
                ),
                hr: () => (
                  <div className="my-6 flex items-center gap-4">
                    <div className="flex-1 h-px bg-gradient-to-r from-transparent via-gray-300 to-transparent"></div>
                    <span className="text-gray-400">‚ú¶</span>
                    <div className="flex-1 h-px bg-gradient-to-r from-transparent via-gray-300 to-transparent"></div>
                  </div>
                ),
                a: ({ href, children }) => (
                  <a href={href} className="inline-flex items-center gap-1 text-blue-600 hover:text-blue-800 underline decoration-blue-300 hover:decoration-blue-500 transition-colors font-medium" target="_blank" rel="noopener noreferrer">
                    {children} <span className="text-xs">‚Üó</span>
                  </a>
                ),
              }}
            >
              {content}
            </ReactMarkdown>
          </div>
        </div>
      </div>
    </div>
  );
};
