import React, { useState, useEffect, useRef, useCallback, memo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FiSend, FiArrowLeft, FiCpu, FiCopy, FiCheck } from 'react-icons/fi';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import { chatAPI } from '../services/api';
import { useFocusTimer } from '../useFocusTimer';
import { FocusTimer } from '../components/FocusTimer';
import APIStatusIndicator from '../components/APIStatusIndicator';

// Copy button component
const CopyButton: React.FC<{ text: string; className?: string }> = ({ text, className = '' }) => {
  const [copied, setCopied] = useState(false);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  return (
    <button
      onClick={handleCopy}
      className={`p-1.5 rounded-md transition-all duration-200 ${copied ? 'bg-green-100 text-green-600' : 'bg-gray-100 hover:bg-gray-200 text-gray-500 hover:text-gray-700'} ${className}`}
      title={copied ? 'Copied!' : 'Copy'}
    >
      {copied ? <FiCheck size={14} /> : <FiCopy size={14} />}
    </button>
  );
};

// Code block with copy button and language label
const CodeBlock: React.FC<{ children: React.ReactNode; className?: string }> = ({ children, className }) => {
  const codeRef = useRef<HTMLPreElement>(null);
  const [copied, setCopied] = useState(false);
  const getCodeText = () => codeRef.current?.textContent || '';

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(getCodeText());
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('Failed to copy:', err);
    }
  };

  return (
    <div className="relative group my-4 rounded-xl overflow-hidden shadow-lg">
      {/* Header bar */}
      <div className="flex items-center justify-between px-4 py-2 bg-gray-800 border-b border-gray-700">
        <div className="flex items-center gap-2">
          <div className="flex gap-1.5">
            <div className="w-3 h-3 rounded-full bg-red-500" />
            <div className="w-3 h-3 rounded-full bg-yellow-500" />
            <div className="w-3 h-3 rounded-full bg-green-500" />
          </div>
          <span className="text-xs text-gray-400 ml-2 font-mono">Code</span>
        </div>
        <button
          onClick={handleCopy}
          className={`flex items-center gap-1.5 px-2.5 py-1 rounded-md text-xs transition-all ${
            copied 
              ? 'bg-green-600 text-white' 
              : 'bg-gray-700 text-gray-300 hover:bg-gray-600 hover:text-white'
          }`}
        >
          {copied ? <FiCheck size={12} /> : <FiCopy size={12} />}
          {copied ? 'Copied!' : 'Copy'}
        </button>
      </div>
      {/* Code content - uniform color */}
      <pre 
        ref={codeRef} 
        className={`bg-gray-900 p-4 overflow-x-auto text-sm font-mono leading-relaxed ${className || ''}`}
        style={{ color: '#e2e8f0' }}
      >
        <code style={{ color: 'inherit', background: 'transparent' }}>{children}</code>
      </pre>
    </div>
  );
};

// Typing indicator component
const TypingIndicator = () => (
  <div className="flex items-center gap-1 p-3">
    <span className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0ms' }} />
    <span className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '150ms' }} />
    <span className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '300ms' }} />
  </div>
);

interface ChatPageProps {
  onLogout: () => void;
}

interface Message {
  id: string;
  userMessage: string;
  aiResponse: string;
  timestamp: Date;
}

const ChatPage: React.FC<ChatPageProps> = memo(({ onLogout }) => {
  const { courseId } = useParams<{ courseId: string }>();
  const navigate = useNavigate();
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const [conversationId, setConversationId] = useState<string | undefined>(undefined);
  const focusTimer = useFocusTimer();
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages, scrollToBottom]);

  useEffect(() => {
    inputRef.current?.focus();
  }, []);

  const handleSendMessage = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();
    const trimmedInput = input.trim();
    if (!trimmedInput || loading) return;

    setLoading(true);
    setInput('');

    try {
      const response = await chatAPI.sendMessage(courseId || 'dsa', trimmedInput, conversationId);

      if (!conversationId && response.data.conversationId) {
        setConversationId(response.data.conversationId);
      }

      const newMessage: Message = {
        id: `msg_${Date.now()}`,
        userMessage: trimmedInput,
        aiResponse: response.data.aiResponse,
        timestamp: new Date(),
      };

      setMessages(prev => [...prev, newMessage]);
    } catch (error) {
      console.error('Failed to send message:', error);
      setMessages(prev => [...prev, {
        id: `error_${Date.now()}`,
        userMessage: trimmedInput,
        aiResponse: 'Sorry, I encountered an error. Please try again.',
        timestamp: new Date(),
      }]);
    } finally {
      setLoading(false);
      inputRef.current?.focus();
    }
  }, [input, loading, courseId, conversationId]);

  const handleKeyDown = useCallback((e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage(e);
    }
  }, [handleSendMessage]);

  const goBack = useCallback(() => navigate('/dashboard'), [navigate]);

  const courseName = courseId?.toUpperCase() || 'Learning';

  return (
    <div className="h-screen flex flex-col bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm flex-shrink-0">
        <div className="max-w-4xl mx-auto px-4 py-3 flex items-center justify-between">
          <div className="flex items-center gap-4">
            <button
              onClick={goBack}
              className="flex items-center gap-2 text-blue-600 hover:text-blue-700 transition-colors"
            >
              <FiArrowLeft size={20} /> Back
            </button>
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl flex items-center justify-center">
                <FiCpu className="text-white" />
              </div>
              <div>
                <h1 className="text-lg font-bold text-gray-900">{courseName} Assistant</h1>
                <p className="text-xs text-gray-500">AI-powered learning companion</p>
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* API Status + Messages Container */}
      <div className="flex-1 overflow-y-auto">
        <div className="max-w-4xl mx-auto px-4 py-4">
          {/* Live API Status Indicator */}
          <div className="mb-4">
            <APIStatusIndicator />
          </div>

          {/* Messages */}
          <div className="space-y-6">
          {messages.length === 0 ? (
            <div className="flex flex-col items-center justify-center h-full min-h-[400px] text-center animate-fade-in">
              <div className="w-20 h-20 bg-gradient-to-br from-blue-100 to-purple-100 rounded-2xl flex items-center justify-center mb-6">
                <FiCpu className="text-4xl text-blue-600" />
              </div>
              <h2 className="text-2xl font-bold text-gray-900 mb-3">
                Start Learning {courseName}!
              </h2>
              <p className="text-gray-600 max-w-md mb-8">
                Ask any question about {courseName} and I'll help you understand it with personalized explanations.
              </p>

              <div className="flex flex-wrap gap-2 justify-center max-w-lg">
                {[
                  `What is ${courseName}?`,
                  'Explain with examples',
                  'Best practices',
                  'Common mistakes'
                ].map((prompt, idx) => (
                  <button
                    key={idx}
                    onClick={() => setInput(prompt)}
                    className="px-4 py-2 bg-white border border-gray-200 rounded-full text-sm text-gray-700 hover:bg-blue-50 hover:border-blue-300 hover:text-blue-600 transition-all"
                  >
                    {prompt}
                  </button>
                ))}
              </div>
            </div>
          ) : (
            messages.map((msg, idx) => (
              <div key={msg.id} className="space-y-4 animate-fade-in" style={{ animationDelay: `${idx * 50}ms` }}>
                {/* User Message */}
                <div className="flex justify-end">
                  <div className="bg-gradient-to-br from-blue-600 to-blue-700 text-white rounded-2xl rounded-br-md px-4 py-3 max-w-[80%] shadow-sm">
                    {msg.userMessage}
                  </div>
                </div>

                {/* AI Response */}
                <div className="flex justify-start">
                  <div className="flex gap-3 max-w-[92%]">
                    <div className="w-10 h-10 bg-gradient-to-br from-purple-500 to-blue-600 rounded-xl flex items-center justify-center flex-shrink-0 mt-1 shadow-lg">
                      <FiCpu className="text-white" size={20} />
                    </div>
                    <div className="relative group bg-white border border-gray-100 rounded-2xl rounded-bl-md px-6 py-5 shadow-lg overflow-hidden">
                      {/* Decorative gradient */}
                      <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500"></div>
                      {/* Copy entire response button */}
                      <div className="absolute right-4 top-4 opacity-0 group-hover:opacity-100 transition-opacity z-10">
                        <CopyButton text={msg.aiResponse} className="bg-white shadow-md border border-gray-200" />
                      </div>
                      <ReactMarkdown 
                        remarkPlugins={[remarkGfm]}
                        components={{
                          // Headers with icons and better styling
                          h1: ({ children }) => (
                            <h1 className="text-xl font-bold text-gray-900 mt-4 mb-3 first:mt-0 pb-2 border-b-2 border-blue-500 flex items-center gap-2">
                              <span className="text-2xl"></span> {children}
                            </h1>
                          ),
                          h2: ({ children }) => (
                            <h2 className="text-lg font-bold text-gray-800 mt-6 mb-3 first:mt-0 flex items-center gap-2 bg-gradient-to-r from-blue-50 to-transparent px-3 py-2 rounded-lg border-l-4 border-blue-500">
                              {children}
                            </h2>
                          ),
                          h3: ({ children }) => (
                            <h3 className="text-base font-semibold text-gray-800 mt-4 mb-2 flex items-center gap-2">
                              <span className="w-2 h-2 bg-blue-500 rounded-full"></span> {children}
                            </h3>
                          ),

                          // Paragraphs
                          p: ({ children }) => <p className="text-gray-700 my-3 leading-relaxed text-[15px]">{children}</p>,

                          // Strong/Bold with highlight
                          strong: ({ children }) => <strong className="font-bold text-gray-900 bg-yellow-100 px-1 rounded">{children}</strong>,

                          // Code blocks with copy button
                          pre: ({ children }) => <CodeBlock>{children}</CodeBlock>,
                          code: ({ className, children, ...props }) => {
                            const isInline = !className;
                            return isInline ? (
                              <code className="bg-gradient-to-r from-blue-50 to-indigo-50 text-blue-700 px-2 py-0.5 rounded-md text-sm font-mono border border-blue-200 shadow-sm" {...props}>
                                {children}
                              </code>
                            ) : (
                              <code className={`${className || ''} text-sm`} {...props}>
                                {children}
                              </code>
                            );
                          },

                          // Tables with better styling
                          table: ({ children }) => (
                            <div className="overflow-x-auto my-4 rounded-xl border border-gray-200 shadow-md">
                              <table className="min-w-full">
                                {children}
                              </table>
                            </div>
                          ),
                          thead: ({ children }) => <thead className="bg-gradient-to-r from-blue-600 to-indigo-600 text-white">{children}</thead>,
                          tbody: ({ children }) => <tbody className="bg-white divide-y divide-gray-100">{children}</tbody>,
                          tr: ({ children }) => <tr className="hover:bg-blue-50 transition-colors">{children}</tr>,
                          th: ({ children }) => (
                            <th className="px-5 py-3 text-left text-xs font-bold uppercase tracking-wider">
                              {children}
                            </th>
                          ),
                          td: ({ children }) => (
                            <td className="px-5 py-3 text-sm text-gray-700 border-b border-gray-100">
                              {children}
                            </td>
                          ),

                          // Lists with better styling
                          ul: ({ children }) => <ul className="my-4 space-y-2 text-gray-700">{children}</ul>,
                          ol: ({ children }) => <ol className="my-4 space-y-2 text-gray-700">{children}</ol>,
                          li: ({ children }) => (
                            <li className="flex items-start gap-3 leading-relaxed pl-1">
                              <span className="flex-shrink-0 w-6 h-6 bg-blue-100 text-blue-600 rounded-full flex items-center justify-center text-xs font-bold mt-0.5"></span>
                              <span className="flex-1">{children}</span>
                            </li>
                          ),

                          // Blockquote / Tip boxes
                          blockquote: ({ children }) => (
                            <blockquote className="my-4 p-4 bg-gradient-to-r from-amber-50 to-yellow-50 border-l-4 border-amber-400 rounded-r-xl shadow-sm">
                              <div className="flex items-start gap-3">
                                <span className="text-xl"></span>
                                <div className="text-gray-700 flex-1">{children}</div>
                              </div>
                            </blockquote>
                          ),

                          // Horizontal rule
                          hr: () => (
                            <div className="my-6 flex items-center gap-4">
                              <div className="flex-1 h-px bg-gradient-to-r from-transparent via-gray-300 to-transparent"></div>
                              <span className="text-gray-400"></span>
                              <div className="flex-1 h-px bg-gradient-to-r from-transparent via-gray-300 to-transparent"></div>
                            </div>
                          ),

                          // Links
                          a: ({ href, children }) => (
                            <a href={href} className="inline-flex items-center gap-1 text-blue-600 hover:text-blue-800 underline decoration-blue-300 hover:decoration-blue-500 transition-colors font-medium" target="_blank" rel="noopener noreferrer">
                              {children} <span className="text-xs"></span>
                            </a>
                          ),
                        }}
                      >
                        {msg.aiResponse}
                      </ReactMarkdown>
                    </div>
                  </div>
                </div>
              </div>
            ))
          )}

          {/* Typing indicator */}
          {loading && (
            <div className="flex justify-start animate-fade-in">
              <div className="flex gap-3 items-center">
                <div className="w-8 h-8 bg-gradient-to-br from-purple-100 to-blue-100 rounded-lg flex items-center justify-center flex-shrink-0">
                  <FiCpu className="text-purple-600" size={16} />
                </div>
                <div className="bg-white border border-gray-100 rounded-2xl px-4 py-2 shadow-sm">
                  <TypingIndicator />
                </div>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
          </div>
        </div>
      </div>

      {/* Input Area */}
      <div className="bg-white border-t border-gray-200 flex-shrink-0">
        <div className="max-w-4xl mx-auto px-4 py-4">
          <form onSubmit={handleSendMessage} className="flex gap-3">
            <input
              ref={inputRef}
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Ask a question..."
              className="flex-1 px-4 py-3 border border-gray-200 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
              disabled={loading}
            />
            <button
              type="submit"
              disabled={loading || !input.trim()}
              className="bg-gradient-to-r from-blue-600 to-purple-600 text-white px-6 py-3 rounded-xl hover:from-blue-700 hover:to-purple-700 transition-all disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2 font-medium shadow-sm"
            >
              {loading ? (
                <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin" />
              ) : (
                <FiSend />
              )}
              Send
            </button>
          </form>
        </div>
      </div>

      {/* Focus Timer Widget */}
      <FocusTimer 
        totalTime={focusTimer.totalTime}
        activeTime={focusTimer.activeTime}
        focusScore={focusTimer.focusScore}
        isActive={focusTimer.isActive}
      />
    </div>
  );
});

export default ChatPage;
